import React from "react";
import styled, { css } from 'styled-components';
import {Link} from "react-router-dom";

const RedRectangle = styled.div`
    background: #81100B;
    height: 441px;
    position: absolute;
    z-index: 0;
    ${props => props.one && css`
        width: 1330px;
        left: 21%;
        top: 0px;
        border-radius: 20px 0px 0px 20px;
        @media only all and (max-width: 1680px) {

        }
    `}
    ${props => props.two && css`
        width: 1234px;
        right: 24.1%;
        top: 0px;
        border-radius: 0px 20px 20px 0px;
        @media only all and (max-width: 1680px) {
            right: 27%;
        }
        @media only all and (min-width: 1920px) {
            right: 29%;
        }
    `}
    @media only all and (max-width: 570px) {
        height: 0;
        width: 0;
    }
`;
const ContainerLungo = styled.div`
    position: relative;
    margin-top: 40px;
    height: 441px;
    margin-left: auto!important;
    margin-right: auto!important;
    display: flex;
    flex-wrap: wrap;
    @media only all and (max-width: 1680px) {
        max-width: 80%!important;
		flex: 0 0 80%!important;
    }
    @media only all and (max-width: 1440px) {
        max-width: 100%!important;
		flex: 0 0 100%!important;
    }
    @media only all and (max-width: 570px) {
        background: #81100B;
        margin-left: 20px!important;
        margin-right: 20px!important;
        display: block;
        max-width: unset!important;
		flex: unset!important;
        margin-top: 60px;
        border-radius: 20px;
        height: auto;
        padding-bottom: 113px;
    }
    ${props => props.two && css`
        @media only all and (max-width: 570px) {
            margin-top: 80px;
        }
    `}

`;
const Logo1 = styled.img`
    z-index: 1;
    position: absolute;
    right: -4%;
    top: -8%;
    @media only all and (max-width: 1680px) {
        right: 0%;
        top: -8%;
    }
    @media only all and (max-width: 570px) {
        right: -3%;
        top: -23%;
    }
    @media only all and (max-width: 348px) {
        top: -17%;
    }
`;
const Logo2 = styled.img`
    z-index: 1;
    position: absolute;
    left: -2%;
    top: -8%;
    @media only all and (max-width: 1680px) {
        left: 0%;
    }
    @media only all and (max-width: 570px) {
        left: -3%;
        top: -15%;
    }
`;
const CarLungo = styled.img`
    z-index: 1;
    position: absolute;
    left: -5%;
    bottom: -10%;
    @media only all and (max-width: 1680px) {
        left: 0%;
    }
    @media only all and (max-width: 570px) {
        width: 253px;
        height: 140px;
        left: -8%;
    }
`;
const CarBreve = styled.img`
    z-index: 1;
    position: absolute;
    right: 0%;
    bottom: -14%;
    @media only all and (max-width: 1680px) {
        right: 15%;
    }
    @media only all and (max-width: 570px) {
        width: 272px;
        height: 181px;
        right: -6%;
        bottom: -10%;
    }
`;
const TitleLungo = styled.div`
    z-index: 1;
    position: relative;
    text-align: center;
    letter-spacing: 0.05em;
    text-transform: uppercase;
    font-weight: bold;
    font-size: 38px;
    @media only all and (max-width: 570px) {
        font-size: 24px;
        margin-left: auto;
        margin-right: auto;
    }
    ${props => props.one && css`
        color: #DAA520;
        margin-left: 23%;
        @media only all and (max-width: 570px) {
            margin-top: 32px;
        }
    `}
    ${props => props.two && css`
        margin-left: 6%;
        @media only all and (max-width: 1680px) {
            margin-left: 8%;
        }
        @media only all and (max-width: 570px) {
            margin-top: 72px;
        }
    `}

`;
const Text = styled.div`
    z-index: 1;
    position: relative;
    font-size: 30px;
    line-height: 36px;
    width: 800px;
    ${props => props.one && css`
        margin-left: 40%;
        margin-top: -5%;
    `}
    ${props => props.two && css`
        text-align: center;
        margin-left: 50%;
        margin-top: -5%;
        margin-right: 6%;
    `}
    ${props => props.tree && css`
        margin-left: 1%;
        margin-top: 0%;
    `}
    ${props => props.four && css`
        margin-left: 6%;
        margin-top: -4%;
    `}
    ${props => props.five && css`
        margin-left: 1%;
        margin-top: -5%;
    `}
    @media only all and (max-width: 570px) {
        width: auto;
        text-align: center;
        font-size: 21px;
        line-height: 100%;
        margin-left: 40px;
        margin-right: 40px;
        margin-top: 32px;
    }
`;
const BigArrow = styled(Link)`
    position: relative;
    top: 0px;
    left: 700px;
    width: 825px;
    height: 77px;
    background: #81100B;
    border-radius: 38.5px;
    text-align: right;
    margin-bottom: 20px;
    margin-top: 25px;
    @media only all and (max-width: 570px) {
        top: 0px;
    }
    @media only all and (max-width: 1680px) {
        left: 35%;
    }
    ${props => props.one && css`
        @media only all and (max-width: 570px) {
            top: 25px;
        }
    `}
    ${props => props.two && css`
        text-align: left;
        left: 325px;
        @media only all and (max-width: 1680px) {
            left: 2%;
        }
        @media only all and (max-width: 570px) {
            top: 25px;
        }
    `}

    @media only all and (max-width: 570px) {
        width: 100%;
        left: 0;
        margin-left: 20px!important;
        margin-right: 20px!important;
        height: auto;

    }
`;
const Arrow = styled.img`
    width: 57px;
    height: 57px;
    background: #1D1D1B;
    border-radius: 40px;
    margin: 10px 20px 10px 0px;
    ${props => props.two && css`
        margin: 10px 0px 10px 20px;
    `}
    @media only all and (max-width: 380px) {
        margin-top: 30px;
    }

`;
const TitleArrow = styled.div`
    font-weight: bold;
    font-size: 43px;
    letter-spacing: 0.01em;
    text-decoration-line: underline;
    text-transform: uppercase;
    color: #FFFFFF;
    padding-top: 5px;
    @media only all and (max-width: 570px) {
        font-size: 24px;
        margin-top: 12px;
        margin-bottom: 20px;
    }
`;
const SmallRedRectangle = styled.div`
    position: relative;
    width: 1420px;
    left: 0px;
    box-shadow: 0px 10px 5px rgba(0, 0, 0, 0.25);
    border-radius: 20px;
    background: #81100B;
    margin-top: 50px;
    margin-right: 15px;
    margin-left: 0px;
    @media only all and (max-width: 570px) {
        width: 100%;
    }

`;
const TitleRedRectangle = styled.div`
    font-weight: bold;
    font-size: 43px;
    letter-spacing: 0.01em;
    text-transform: uppercase;
    color: #FFFFFF;
    text-align: center;
    margin-left: 250px;
    @media only all and (max-width: 570px) {
        font-size: 24px;
        margin-left: 20px;
        margin-right: 20px;
        padding-top: 30px;
        padding-bottom: 30px;

    }
`;
const BlackRectangle = styled.div`
    position: absolute;
    right: 0px;
