import React from "react";
import styled, { css } from 'styled-components';
import {Link} from "react-router-dom";

const RedRectangle = styled.div`
    background: #81100B;
    width: 1330px;
    height: 441px;
    position: absolute;
    z-index: 0;

    ${props => props.one && css`
        right: 0px;
        top: 0px;
        border-radius: 20px 0px 0px 20px;
    `}
    ${props => props.two && css`
        left: 0px;
        top: 0px;
        border-radius: 0px 20px 20px 0px;
    `}
`;
const ContainerLungo = styled.div`
    position: relative;
    margin-top: 40px;
`;
const Logo1 = styled.img`
    z-index: 1;
    position: absolute;
    right: 310px;
    top: -42px;
`;
const Logo2 = styled.img`
    z-index: 1;
    position: absolute;
    left: 250px;
    top: -42px;
`;
const CarLungo = styled.img`
    z-index: 1;
    position: relative;
    right: 330px;
    bottom: -35px;
    @media only all and (min-width: 1920px) {
        right: 250px;
    }
`;
const CarBreve = styled.img`
    z-index: 1;
    position: relative;
    left: 630px;
    bottom: -35px;
`;
const TitleLungo = styled.div`
    z-index: 1;
    position: relative;
    text-align: center;
    letter-spacing: 0.05em;
    text-transform: uppercase;
    font-weight: bold;
    font-size: 38px;
    ${props => props.one && css`
        left: 615px;
        top: 0px;
        color: #DAA520;
        @media only all and (min-width: 1920px) {
            left: 670px;
        }
    `}
    ${props => props.two && css`
        left: 365px;
        top: 15px;
    `}
`;
const Text = styled.div`
    z-index: 1;
    position: relative;
    font-size: 30px;
    line-height: 36px;
    ${props => props.one && css`
        width: 586px;
        right: -15px;
        top: 120px;
        @media only all and (min-width: 1920px) {
            right: -100px;
        }
    `}
    ${props => props.two && css`
        width: 540px;
        left: 895px;
        top: 165px;
        @media only all and (min-width: 1920px) {
            left: 980px;
        }

    `}
    ${props => props.tree && css`
        width: 348px;
        right: 485px;
        top: 130px;
    `}
    ${props => props.four && css`
        width: 516px;
        right: 773px;
        top: 200px;
    `}
    ${props => props.five && css`
        width: 356px;
        left: 307px;
        top: 210px;
    `}
`;
const BigArrow = styled.div`
    position: relative;
    top: 0px;
    left: 700px;
    width: 825px;
    height: 77px;
    background: #81100B;
    border-radius: 38.5px;
    text-align: right;
    margin-bottom: 20px;
    margin-top: 15px;
    @media only all and (max-width: 1440px) {
        left: 35%;
    }
    ${props => props.two && css`
        text-align: left;
        margin: 0;
        left: 275px;
    `}
`;
const Arrow = styled.img`
    width: 57px;
    height: 57px;
    background: #1D1D1B;
    border-radius: 40px;
    margin: 0 15px 10px 15px;

`;
const TitleArrow = styled.a`
    font-weight: bold;
    font-size: 43px;
    letter-spacing: 0.01em;
    text-decoration-line: underline;
    text-transform: uppercase;
    color: #FFFFFF;
    &:hover {
        color: #FFFFFF;
    }
`;
const SmallRedRectangle = styled.div`
    position: relative;
    width: 1420px;
    left: 0px;
    box-shadow: 0px 10px 5px rgba(0, 0, 0, 0.25);
    border-radius: 20px;
    background: #81100B;
    margin-top: 77px;
    margin-right: 15px;
    margin-bottom: 15px;
    margin-left: 0px;

`;
const TitleRedRectangle = styled.div`
    font-weight: bold;
    font-size: 43px;
    letter-spacing: 0.01em;
    text-transform: uppercase;
    color: #FFFFFF;
    text-align: center;
    margin-left: 250px;
`;
const BlackRectangle = styled.div`
    position: absolute;
    right: 0px;
    width: 1560px;
    height: 310px;
    background: #10100F;
    box-shadow: 0px 10px 5px rgba(0, 0, 0, 0.25);
    border-radius: 10px;

`;
const BlackContainer = styled.div`
    margin-right: 400px;
    margin-left: 360px;
    text-align: center;
    padding-top: 10px;
    flex-wrap: wrap;
    display: flex;
`;
const BlackTitle = styled.div`
    font-style: normal;
    font-weight: 300;
    font-size: 24px;
    letter-spacing: 0.05em;
    text-transform: uppercase;
    color: #FFFFFF;
    margin-top: 10px;
`;
const BlackText = styled.div`
    font-style: normal;
    font-weight: 300;
    font-size: 20px;
    letter-spacing: 0.05em;
    color: #FFFFFF;
    margin-top: 30px;
`;
const Black = styled.div`
    height: 310px;
    margin-bottom: 60px;
    margin-top: 22px;
`;
function VantaggiNoleggio() {
    return(
    <>
    <ContainerLungo className="row">
        <RedRectangle one></RedRectangle>
        <Logo1 src="./images/logo2.svg"></Logo1>
        <TitleLungo one>VANTAGGI NOLEGGIO LUNGO TERMINE</TitleLungo>
        <Text one>Scegli l’innovazione, guida la tua prossima auto senza fatica e senza pensieri</Text>
        <Text two>Elettrica, ibrida, benzina o diesel scegli tu come migliorare il tuo stile di vita</Text>
        <CarLungo src="./images/car_lungo.png"></CarLungo>
    </ContainerLungo>
    <ButtonScopri link={"/Long_Rent_Benefits"}/>
    <ContainerLungo className="row">
        <RedRectangle two></RedRectangle>
        <Logo2 src="./images/logo3.svg"></Logo2>
