import React from "react";
import styled, { css } from 'styled-components';
const RedRectangle = styled.div`
    background: #81100B;
    width: 1330px;
    height: 441px;
    position: absolute;
    z-index: 0;

    ${props => props.one && css`
        right: 30px;
        top: 0px;
        border-radius: 20px 0px 0px 20px;
    `}
    ${props => props.two && css`
        left: 0px;
        top: 0px;
        border-radius: 0px 20px 20px 0px;
    `}
`;
const ContainerLungo = styled.div`
    position: relative;
    margin-top: 40px;
`;
const Logo1 = styled.img`
    z-index: 1;
    position: absolute;
    right: 310px;
    top: -42px;
`;
const Logo2 = styled.img`
    z-index: 1;
    position: absolute;
    left: 250px;
    top: -42px;
`;
const CarLungo = styled.img`
    z-index: 1;
    position: relative;
    right: 330px;
    bottom: -35px;
    @media only all and (min-width: 1920px) {
        left: 330px;
    }
`;
const CarBreve = styled.img`
    z-index: 1;
    position: relative;
    left: 630px;
    bottom: -35px;
`;
const TitleLungo = styled.div`
    z-index: 1;
    position: relative;
    text-align: center;
    letter-spacing: 0.05em;
    text-transform: uppercase;
    font-weight: bold;
    font-size: 38px;
    ${props => props.one && css`
        left: 615px;
        top: 0px;
        color: #DAA520;
        @media only all and (min-width: 1920px) {
            left: 710px;
        }
    `}
    ${props => props.two && css`
        left: 365px;
        top: 15px;
    `}
`;
const Text = styled.div`
    z-index: 1;
    position: relative;
    font-size: 30px;
    line-height: 36px;
    ${props => props.one && css`
        width: 586px;
        right: -15px;
        top: 120px;
        @media only all and (min-width: 1920px) {
            right: -100px;
        }
    `}
    ${props => props.two && css`
        width: 540px;
        left: 895px;
        top: 165px;
        @media only all and (min-width: 1920px) {
            left: -345px;
            top: 250px;
        }

    `}
    ${props => props.tree && css`
        width: 348px;
        right: 485px;
        top: 130px;
    `}
    ${props => props.four && css`
        width: 516px;
        right: 773px;
        top: 200px;
    `}
    ${props => props.five && css`
        width: 356px;
        left: 307px;
        top: 210px;
    `}
`;
const BigArrow = styled.div`
    position: relative;
    z-index: 1;
    top: 0px;
    left: 700px;
    width: 825px;
    height: 77px;
    background: #81100B;
    border-radius: 38.5px;
    text-align: right;
    margin-bottom: 20px;
    margin-top: 15px;
    ${props => props.two && css`
        text-align: left;
        margin: 0;
        left: 275px;
    `}
`;
const Arrow = styled.img`
    width: 57px;
    height: 57px;
    background: #1D1D1B;
    border-radius: 40px;
    margin: 0 15px 10px 15px;

`;
const TitleArrow = styled.a`
    font-weight: bold;
    font-size: 43px;
    letter-spacing: 0.01em;
    text-decoration-line: underline;
    text-transform: uppercase;
    color: #FFFFFF;
    &:hover {
        color: #FFFFFF;
    }
`;
const SmallRedRectangle = styled.div`
    position: relative;
    width: 1420px;
    left: 0px;
    box-shadow: 0px 10px 5px rgba(0, 0, 0, 0.25);
    border-radius: 20px;
    background: #81100B;
    margin-top: 77px;
    margin-right: 15px;
    margin-bottom: 15px;
    margin-left: 15px;

`;
const TitleRedRectangle = styled.div`
    font-weight: bold;
    font-size: 43px;
    letter-spacing: 0.01em;
    text-transform: uppercase;
    color: #FFFFFF;
    text-align: center;
    margin-left: 250px;
`;
const BlackRectangle = styled.div`
    position: absolute;
    right: 0px;
    width: 1560px;
    height: 310px;
    background: #10100F;
    box-shadow: 0px 10px 5px rgba(0, 0, 0, 0.25);
    border-radius: 10px;

`;
const BlackContainer = styled.div`
    margin-right: 400px;
    margin-left: 360px;
    text-align: center;
    padding-top: 10px;
`;
const BlackTitle = styled.div`
    font-style: normal;
    font-weight: 300;
    font-size: 24px;
    letter-spacing: 0.05em;
    text-transform: uppercase;
    color: #FFFFFF;
    margin-top: 10px;
`;
const BlackText = styled.div`
    font-style: normal;
    font-weight: 300;
    font-size: 20px;
    letter-spacing: 0.05em;
    color: #FFFFFF;
    margin-top: 30px;
`;
const Black = styled.div`
    height: 310px;
    margin-bottom: 60px;
    margin-top: 22px;
`;
function VantaggiNoleggio() {
    return(
    <>
    <ContainerLungo className="row">
        <RedRectangle one></RedRectangle>
        <Logo1 src="./images/logo2.svg"></Logo1>
        <TitleLungo one>VANTAGGI NOLEGGIO LUNGO TERMINE</TitleLungo>
        <Text one>Scegli l’innovazione, guida la tua prossima auto senza fatica e senza pensieri</Text>
        <Text two>Elettrica, ibrida, benzina o diesel scegli tu come migliorare il tuo stile di vita</Text>
        <CarLungo src="./images/car_lungo.png"></CarLungo>
    </ContainerLungo>
    <ButtonScopri/>
    <ContainerLungo className="row">
        <RedRectangle two></RedRectangle>
        <Logo2 src="./images/logo3.svg"></Logo2>
        <TitleLungo two>VANTAGGI NOLEGGIO BREVE TERMINE</TitleLungo>
        <Text tree>Risolve le difficoltà subito</Text>
        <Text four>Noleggia un giorno, un mese, un anno</Text>
        <Text five>Come vuoi quando vuoi tu</Text>
        <CarBreve src="./images/car_breve.png"></CarBreve>
    </ContainerLungo>
    <BigArrow two>
        <Arrow src="./images/expand_arrow2.svg"></Arrow>
        <TitleArrow>SCOPRI L’OFFERTA</TitleArrow>
    </BigArrow>
    <div>
        <SmallRedRectangle>
            <TitleRedRectangle>PERCHE' NOLEGGIARE UNA AUTO E'<br></br> UNA GRANDE SOLUZIONE</TitleRedRectangle>
        </SmallRedRectangle>
    </div>
    <ButtonScopri/>
    <Black>
        <BlackRectangle>
        </BlackRectangle>
            <BlackContainer className="row">
            <div className="col-sm-4">
                <img src="./images/Business.svg"></img>
                <BlackTitle>Per le aziende</BlackTitle>
                <BlackText>Un solo fornitore Una sola unica fattura al mese I costi sono sempre fissi Molti vantaggi fiscali</BlackText>
            </div>
            <div className="col-sm-4">
                <img src="./images/User.svg"></img>
                <BlackTitle>Per i privati</BlackTitle>
                <BlackText>Elimina la svalutazione del bene acquistato, costi certi e sicuri ogni mese senza sorprese</BlackText>
            </div>
            <div className="col-sm-4">
                <img src="./images/People.svg"></img>
                <BlackTitle>Per tutti</BlackTitle>
                <BlackText>Il noleggio è l’innovazione dello stile di vita, è comodo e facile e ti fa stare meglio</BlackText>
            </div>
            </BlackContainer>

    </Black>
    </>
    );
}
function ButtonScopri() {
    return(
    <>
    <BigArrow>
        <TitleArrow>SCOPRI L’OFFERTA</TitleArrow>
        <Arrow src="./images/expand_arrow1.svg"></Arrow>
    </BigArrow>
    </>
    );
}
export {VantaggiNoleggio};
