import React from "react";
import {Title} from './title';
import styled, { css } from 'styled-components';
import {VantaggiNoleggio} from './vantaggi_noleggio';
import { Link } from "react-router-dom";
import { useEffect, useRef } from "react";
import { useLocation } from "react-router-dom";


const Container = styled.div`
    font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Arial, sans-serif;
    @media only all and (max-width: 1440px) {
        max-width: 100%!important;
        flex: 0 0 100%!important;
    }
`;
const RedRectangle = styled.div`
    height: 264px;
    background: #8D120C;
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    border-radius: 20px;
    position: relative;
`;
const RedTitle = styled.div`
    font-family: Poppins;
    font-style: normal;
    font-weight: bold;
    font-size: 48px;
    text-transform: uppercase;
    color: #FFFFFF;
    padding-top: 60px;
`;
const RedText = styled.div`
    font-family: Poppins;
    font-style: normal;
    font-weight: bold;
    font-size: 40px;
    text-transform: uppercase;
    color: #FFFFFF;
    padding-top: 40px;
`;
const Logo = styled.img`
    position: absolute;
    right: -1.5%;
    top: -35%;
`;
const BlackSquare = styled.div`
    font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Arial, sans-serif;
    position: relative;
    height: 601px;
    background: #1D1D1B;
    border-radius: 20px;
    margin-top: 40px;
    margin-bottom: 24px;
`;
const BlackText = styled.div`
    font-weight: bold;
    font-size: 32px;
    line-height: 42px;
    text-transform: uppercase;
    color: #FFFFFF;
    position: relative;
    z-index: 2;
    ${props => props.one && css`
        right: 145px;
        top: 80px;
    `}
    ${props => props.two && css`
        right: -20px;
        top: 160px;
    `}
    ${props => props.tree && css`
        right: 40px;
        top: 215px;
    `}
    ${props => props.four && css`
        top: 265px;
        left: 125px;
    `}
`;
const CreditCard = styled.img`
    position: absolute;
    left: -13%;
    top: -26%;
    @media only all and (max-width: 1440px) {
        left: -28%;
    }
`;
const Like = styled.img`
    position: absolute;
    right: -10%;
    top: -23%;
    @media only all and (max-width: 1440px) {
        right: -30%;
    }
`;
const CarVendo = styled.img`
    position: absolute;
    left: -10%;
    bottom: -18%;
    @media only all and (max-width: 1440px) {
        left: -27%;
    }
`;
const Circle = styled.div`
    position: absolute;
    width: 100px;
    height: 100px;
    background: #8D120C;
    border-radius: 100px;
    ${props => props.one && css`
        left: 120px;
        bottom: 230px;
        @media only all and (max-width: 1440px) {
            left: 10px;
        }
    `}
    ${props => props.two && css`
        right: 85px;
        top: 170px;
        @media only all and (max-width: 1440px) {
            right: 10px;
        }
    `}
    ${props => props.tree && css`
        right: 85px;
        bottom: 85px;
        @media only all and (max-width: 1440px) {
            right: 10px;
        }
    `}

`;
const RedArrowContainer = styled.div`
    position: relative;
    height: 80px;
`;
const RedArrow = styled.button`
    position: absolute;
    top: 0;
    right: 0;
    width: 540px;
    height: 78px;
    background: #8D120C;
    border-radius: 38.5px;
    padding-top: 10px;
    padding-bottom: 10px;
    padding-left: 35px;
    padding-right: 10px;
`;
const Contatto = styled.div`
    text-decoration-line: underline;
    text-transform: uppercase;
    font-weight: 700;
    font-size: 32px;
    padding-left: 0;
    padding-right: 0;
    padding-top: 5px;
    color: #FFFFFF;
`;
const BlackArrow = styled.img`
    width: 57px;
    height: 57px;
    background: #1D1D1B;
    border-radius: 40px;
    margin: 0 10px 10px 15px;
`;
const TitleGuarda = styled.div`
    font-weight: bold;
    font-size: 34px;
    line-height: 41px;
    text-transform: uppercase;
    color: #FFFFFF;
    margin-top: 40px;
    margin-bottom: 24px;
`;

const BlackRectangle = styled.div`
    position: absolute;
    top: 0;
    left: -400px;
    background: #1D1D1B;
    width: 1230px;
    height: 446px;
    border-radius: 20px;
    z-index: 0;
`;
const Video = styled.iframe`
    text-align: right;
    position: relative;
    z-index: 1;
    border: none;
    outline: none;
    margin-top: 24px;
`;
const InfoTitle = styled.div`
    font-weight: bold;
    font-size: 20px;
    line-height: 24px;
    text-transform: uppercase;
    color: #FFFFFF;
    background: #1D1D1B;
    border-radius: 20px;
    margin-left: 50px;
    padding-top: 25px;
    padding-bottom: 25px;
`;
const InfoTextBox = styled.div`
    background: #1D1D1B;
    border-radius: 20px;
    font-weight: normal;
    font-size: 20px;
    color: #FFFFFF;
    text-align: left;
    padding-left: 10px;
    padding-bottom: 20px;
    margin-top: 15px;
    margin-left: 50px;
`;
const SmallIcon = styled.img`
    width: 30px;
    height: 30px;
    margin-right: 10px;  
    ${props => props.one && css`
        margin-top: 15px;
    `}
`;
const InfoText = styled.div`
    padding-top: 40px;
    ${props => props.one && css`
        margin-left: 38px;
    `}
`;
const BigText = styled.div`
    font-style: italic;
    font-weight: normal;
    font-size: 30px;
    line-height: 66px;
    text-align: left;
    & > div{
        margin-top: 60px;
    }
`;
const BigCircle = styled.div`
    {/*Pureshka252203$*/}
    position: absolute;
    width: 570px;
    height: 570px;
    background: #8D120C;
    border-radius: 700px;
    ${props => props.one && css`
        right: -600px;
        top: 710px;
    `}
    ${props => props.two && css`
        left: -600px;
        top: 1300px;
    `}
    ${props => props.tree && css`
        right: -600px;
        top: 2000px;
    `}

`;
function Vendo_compro_auto() {
    const testRef = useRef(null);
    const scrollToElement = () => testRef.current.scrollIntoView();
    return(
        <>
        <Title/>
        <Container className="col-sm-8 mx-auto text-center"> 
        <img src="./static/images/mult_car.png"></img>
        <RedRectangle> 
            <Logo src="./static/images/logo4.svg"></Logo>
            <RedTitle>
                Perch√® vendere la tua auto a noi?
            </RedTitle>
            <RedText>
                Perch√® conviene!
