import React, {useState} from "react";
import styled from 'styled-components';
import {Link} from "react-router-dom";
const Menu = styled.div`
    font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Arial, sans-serif;
    text-align: center;
    position: absolute;
    top: 0;
    right: 0;
    z-index: 3;
    background: #8D120C;
    font-weight: 400;
    font-size: 24px;
    line-height: 100%;

`;
const Close = styled.img`
    cursor: pointer;
    text-align: right;
`;
const Icon = styled.img`

`;
const Drop = styled.div`
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    cursor: pointer;
`;
function DropMenu(props) {
    const [isOpen, setIsOpen] = useState(false);
    const toggling = () => setIsOpen(!isOpen);
    return(
    <>
    <Menu className="row">
        <Close src="static/images/Close.svg" onClick={props.toggling}></Close>
        <Link to="/" className="col-12"><Icon src="/static/images/Home_Page.svg"/>HOME</Link>
        <div className="col-12" onClick={toggling}><Icon src="/static/images/Lease.svg"/>NOLEGGIO OFFERTE</div>
        {isOpen && (
        <Drop>
            <Link to="/Long_Rent_Benefits" className="col-12"><div>Lungo termine</div></Link>
            <Link to="/Short_Rent_Benefits" className="col-12"><div>Breve termine</div></Link>
        </Drop>
        ) }
        <Link to="/ri_noleggio" className="col-12"><Icon src="/static/images/Landlord.svg"/>RI-NOLEGGIO</Link>
        <Link to="/vendo_compro_auto" className="col-12"><Icon src="/static/images/Carpool.svg"/>VENDO COMPRO AUTO</Link>
        <Link to="/service" className="col-12"><Icon src="/static/images/Car_Service.svg"/>SERVICE</Link>
        <Link to="/contatti" className="col-12"><Icon src="/static/images/Address.svg"/>CONTATTI</Link>
    </Menu>
    </>
    );
}
export default DropMenu;
