import React from "react";
import {Title} from './title';
import {VantaggiNoleggio} from './vantaggi_noleggio';
import styled, { css } from 'styled-components';
import { Link } from "react-router-dom";
const RedRectangleContainer = styled.div`
    position: relative;
    z-index: 1;
    margin-bottom: 150px;
    &>div{
        margin-bottom: 35px;
    }
    & div>div{
        font-style: normal;
        font-weight: 400;
        font-size: 30px;
        line-height: 34px;
        padding: 0;
        @media only all and (max-width: 570px) {
            font-size: 24px;
            text-align: center;
        }
    }
    & div>img{
        margin-right: 40px;
        @media only all and (max-width: 570px) {
            display: none;
        }
    }
    & div:nth-child(4){
        margin-left: 50px !important;
        margin-right: 250px !important;
        @media only all and (max-width: 570px) {
            margin-left: 0px !important;
            margin-right: 0px !important;
        }
    }
    & div:nth-child(5){
        margin-left: 150px !important;
        margin-right: 460px!important;
        @media only all and (max-width: 570px) {
            margin-left: 0px !important;
            margin-right: 0px !important;
        }
    }
    @media only all and (max-width: 570px) {
        background: #8D120C;
        margin-top: 60px;
        padding-bottom: 44px;
        padding-top: 24px;
        margin-bottom: 130px;
    }
`;
const RedRectangle = styled.div`
    position: absolute;
    top: 0;
    left: -306px;
    background: #8D120C;
    width: 1515px;
    height: 546px;
    z-index: 0;
    @media only all and (max-width: 570px) {
        display: none;
    }
`;
const Container = styled.div`
    font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Arial, sans-serif;
    @media only all and (max-width: 1440px) {
        max-width: 100%!important;
        flex: 0 0 100%!important;
    }
`;
const TitleRinoleggo = styled.div`
    margin-top: 60px;
    font-family: 'Poppins';
    font-style: normal;
    font-weight: 900;
    font-size: 60px;
    letter-spacing: 0.05em;
    text-decoration-line: underline;
    text-transform: uppercase;
    color: #1D1D1B;
    position: relative;
    z-index: 1;
    @media only all and (max-width: 570px) {
        font-size: 30px;
        margin-top: 0px;
    }
`;
const Logo = styled.img`
    position: absolute;
    right: -8%;
    top: -15%;
    @media only all and (max-width: 1440px) {
        right: 9%;
    }
    @media only all and (max-width: 570px) {
        width: 78px;
        height: 89px;
        right: -3%;
        top: -7.8%;
    }

`;
const CarRi = styled.img`
    position: absolute;
    right: -20%;
    bottom: -30%;
    @media only all and (max-width: 1440px) {
        right: -23%;
    }
    @media only all and (max-width: 570px) {
        width: auto;
        height: 145px;
        right: -5%;
        bottom: -17%;
        z-index: 1;
    }
`;
const BlueArrow = styled.img`
    position: absolute;
    left: -25%;
    bottom: 20%;
    @media only all and (max-width: 570px) {
        width: 111px;
        height: 111px;
        left: -2.3%;
        top: -8%;
    }
`;
const CreditCard = styled.img`
    position: absolute;
    right: -17%;
    bottom: 17%;
    @media only all and (max-width: 1440px) {
        display: none;
    }
    @media only all and (max-width: 570px) {
        display: block;
        width: 112px;
        height: 97px;
        right: -3%;
        bottom: -6%;
        z-index: 2;
    }
`;
const Bike = styled.img`
    position: absolute;
    left: -21%;
    bottom: -70%;
    @media only all and (max-width: 570px) {
        width: auto;
        height: 200px;
        left: -13%;
        bottom: -18%;
    }
    @media only all and (max-width: 379px) {
        display: none;
    }
`;
const RedArrowContainer = styled.div`
    position: relative;
    height: 80px;
    margin-bottom: 100px;
    @media only all and (max-width: 570px) {
        height: auto;
        margin-bottom: 50px;
        & a:hover{
            text-decoration-line: none;
        }
    }
`;
const RedArrow = styled.button`
    position: absolute;
    top: 0;
    right: 0;
    height: 78px;
    width: 760px;
    background: #8D120C;
    border-radius: 38.5px;
    padding-top: 10px;
    padding-bottom: 10px;
    padding-left: 15px;
    padding-right: 10px;
    @media only all and (max-width: 570px) {
        width: auto;
        height: auto;
        position: relative;
    }
`;
const BlackArrow = styled.img`
    width: 57px;
    height: 57px;
    background: #1D1D1B;
    border-radius: 40px;
    margin: 0 10px 0px 15px;
`;
const WhatsUp = styled.img`
    width: 60px;
    height: 60px;
    margin-right: 20px;
`;
const Mail = styled.img`
    width: 60px;
    height: 60px;
    margin-right: 10px;
`;

const Chiama = styled.div`
    font-weight: 700;
    font-size: 24px;
    text-transform: uppercase;
    line-height: 30px;
    text-align: left;
    color: #FFFFFF;
    @media only all and (max-width: 570px) {
        font-size: 20px;
        text-align: center;
    }
`;
const Icons = styled.div`
    @media only all and (max-width: 570px) {
        max-width: 80%!important;
        flex: 0 0 80%!important;
        margin-right: auto;
        margin-left: auto;
    }
`;
function Ri_noleggio() {
    return(
        <>
        <Title/>
        <Container className="col-sm-8 mx-auto">
        <RedRectangle>
        </RedRectangle>
        <RedRectangleContainer>
            <TitleRinoleggo className="text-center">RINOLEGGIO</TitleRinoleggo>
            <div className="row">
                <img src="/static/images/Thick_Arrow.svg"/>
                <div className="col-sm">Puoi avere una spesa inferiore approfittando delle offerte del Ri Noleggio</div>
            </div>
            <div className="row">
                <img src="/static/images/Thick_Arrow.svg"/>
                <div className="col-sm">Sono auto che godono degli stessi vantaggi del a noleggio normal</div>
            </div>
            <div className="row">
                <img src="/static/images/Thick_Arrow.svg"/>
                <div className="col-sm">La differenza sta nel fatto che il Ri Noleggio ti permette dì scegliere tra delle auto che hanno già percorso dei km. 10, 30, 50 mila, dipende da cosa si trova e da cosa scegli tu</div>
            </div>
            <div className="row">
                <img src="/static/images/Thick_Arrow.svg"/>
                <div className="col-sm">Serve velocità dì decisione, il risparmio può essere anche a tripla cifra al mese!</div>
            </div>
            <Logo src="./static/images/logo5.svg"></Logo>
            <CarRi src="./static/images/Car_ri.png"></CarRi>
            <BlueArrow src="./static/images/Blue_Arrow.png"></BlueArrow>
            <CreditCard src="./static/images/Credit_cart2.png"></CreditCard>
            <Bike src="/static/images/Bike.png"></Bike>
        </RedRectangleContainer>
        <RedArrowContainer>
            <Link to="/contatti">
                <RedArrow className="row">
                    <Chiama className="col-sm">Chiama o lascia i tuoi dati per conoscere tutte le offerte</Chiama>
                    <Icons>
                        <WhatsUp src="/static/images/whatsapp.svg"></WhatsUp>
                        <Mail src="/static/images/mail.png"></Mail>
                        <BlackArrow src="/static/images/Arrow_bottom.svg"></BlackArrow>
                    </Icons>
                </RedArrow>
            </Link>
        </RedArrowContainer>
        </Container>
        <VantaggiNoleggio/>
        </>
    );
}
export {Ri_noleggio};
