import React, {useState} from "react";
import styled, {css} from 'styled-components';
import {Link} from "react-router-dom";
const Menu = styled.div`
    font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Arial, sans-serif;
    position: absolute;
    top: 0;
    right: 0;
    z-index: 3;
    background: #8D120C;
    font-weight: 400;
    font-size: 22px;
    line-height: 100%;
    margin-left: auto;
    width: 330px;

`;
const Close = styled.div`
    cursor: pointer;
    position: relative;
    text-align: right;
    margin-left: auto;
    margin-top: 20px;
    margin-right: 20px;
`;
const Icon = styled.img`
    margin-right: 15px;
`;
const SmallArrow = styled.img`
    margin-left: 10px;
    width: 15px;
    height: 15px;
`;
const Drop = styled.div`
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    text-transform: uppercase;

`;
const MainLink = styled.div`
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
`;
const StyledLink = styled(Link)`
    margin-top: 20px;
    margin-bottom: 20px;
`;
const ContactMenu = styled.div`
    margin-top: 25px;
    padding-left: 5px;
    ${props => props.up && css`
        margin-top: 35px;
        padding-left: 15px;
    `}  
`;
const LogoMenu = styled.div`
    margin-top: 25px;
    margin-left: 15px;
    margin-bottom: 10px;
`;
const SmallIcon = styled.img`
    width: 41px;
    height: 41px;
    margin-right: 10px;
`;
function DropMenu(props) {
    const [isOpen, setIsOpen] = useState(false);
    const toggling = () => {
        setIsOpen(!isOpen);
        Noleggio && css`box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25)`;
    };
    const Noleggio = styled.div`
        cursor: pointer;
        ${ isOpen && css`
            box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25)
        `}
    `;
    return(
    <>
    <Menu className="row">
        <MainLink className="row">
            <Close><img src="static/images/Close.svg" onClick={props.toggling}></img></Close>
            <StyledLink onClick={props.toggling} to="/" className="col-12"><Icon src="/static/images/Home_Page.svg"/>HOME</StyledLink>
            <Noleggio className="col-12" onClick={toggling}><Icon src="/static/images/Lease.svg"/>NOLEGGIO OFFERTE<SmallArrow src="/static/images/very_small_arrow.svg"></SmallArrow></Noleggio>
            {isOpen && (
            <Drop className="row">
                <StyledLink onClick={props.toggling} to="/Long_Rent_Benefits" className="col-12"><div>Lungo termine</div></StyledLink>
                <StyledLink onClick={props.toggling} to="/Short_Rent_Benefits" className="col-12"><div>Breve termine</div></StyledLink>
            </Drop>
            ) }
            <StyledLink onClick={props.toggling} to="/ri_noleggio" className="col-12"><Icon src="/static/images/Landlord.svg"/>RI-NOLEGGIO</StyledLink>
            <StyledLink onClick={props.toggling} to="/vendo_compro_auto" className="col-12"><Icon src="/static/images/Carpool.svg"/>VENDO COMPRO AUTO</StyledLink>
            <StyledLink onClick={props.toggling} to="/service" className="col-12"><Icon src="/static/images/Car_Service.svg"/>SERVICE</StyledLink>
            <StyledLink onClick={props.toggling} to="/contatti" className="col-12"><Icon src="/static/images/Address.svg"/>CONTATTI</StyledLink>
        </MainLink>
        <ContactMenu className="col-12" up>
            <div><img src="/static/images/whatsapp.svg" className="whatsup"></img>+39 393 989 3909</div>
        </ContactMenu>
        <ContactMenu className="col-12">
            <img className="header__location__img" src="/static/images/location.svg" alt=""></img>
            <div className="header__location__text">
                <div className="header__city">TRENTO</div>
                <div className="header__address">Via Praga N. 5</div>
            </div>
        </ContactMenu>
        <ContactMenu className="col-12">
            <img className="header__location__img" src="/static/images/location.svg" alt=""></img>
            <div className="header__location__text">
                <div className="header__city">SEREGNO</div>
                <div className="header__address">Via Montorfano N.74/76</div>
            </div>
        </ContactMenu>
        <ContactMenu className="col-12">
            <img className="header__location__img" src="/static/images/location.svg" alt=""></img>
            <div className="header__location__text">
                <div className="header__city">OLBIA</div>
                <div className="header__address">Via Liguria N.1</div>
            </div>
        </ContactMenu>
        <LogoMenu>
            <SmallIcon src="/static/images/facebook.svg"></SmallIcon>
            <SmallIcon src="/static/images/whatsapp.svg"></SmallIcon>
            <a href="https://www.youtube.com/channel/UCRS0cuSeg3y2w6Hq7rQsOvg/featured" target="_blank"><SmallIcon src="/static/images/youtube.svg"></SmallIcon></a>
        </LogoMenu>
    </Menu>
    </>
    );
}
export default DropMenu;
