import React from "react";
import styled from 'styled-components';
const StyledCard = styled.div`
    background: #F1EBE3;
    margin-bottom: 50px;
    font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Arial, sans-serif;
    font-style: normal;
    padding-bottom: 7px;
`;
const Line = styled.div`
    background: #8D120C;
    height: 10px;
    margin: 0 20px;
`;
const Noleggio = styled.div`
    font-weight: bold;
    font-size: 14px;
    letter-spacing: 0.05em;
    text-transform: uppercase;
    text-align: left!important;
    color: #000;
    padding-left: 20px;
    padding-top: 8px;
`;
const Row = styled.div`
    padding-left: 24px;
    padding-right: 36px;
    @media only all and (max-width: 1440px) {
        max-width: 100%!important;
        flex: 0 0 100%!important;
    }
`;
const Cambia = styled.div`
    font-weight: bold;
    font-size: 17px;
    letter-spacing: 0.05em;
    color: #FFFFFF;
    text-align: right!important;
    background: #8D120C;
    border-radius: 19px;
    position: absolute;
    right: 5px;
    top: -12px;
    z-index: 0;
    padding: 10px;
`;
const Name = styled.div`
    font-weight: bold;
    font-size: 14px;
    letter-spacing: 0.05em;
    text-transform: capitalize;
    color: #000000;
    text-align: left!important;
    margin: 8px 20px;
`;
const PriceMounth = styled.div`
    font-weight: normal;
    font-size: 24px;
    letter-spacing: 0.05em;
    text-transform: capitalize;
    color: #01579B;
    text-align: right!important;
    margin-right: 20px;
`;
const Characteristics = styled.div`
    font-weight: normal;
    font-size: 14px;
    letter-spacing: 0.05em;
    text-transform: capitalize;
    color: #828282;
    margin: 3px 20px 0 20px;
    position: relative;

`;
const GreyLine = styled.div`
    border: 1px solid #BDBDBD;
    height: 0px;
    margin-bottom: 5px;

`;
function Cart(probs) {
    return(
    <>
    <div className="col-sm-4" >
        <StyledCard className="mx-auto" id={probs.id}>
            <Noleggio>noleggio {probs.data.term}<br></br> TERMINE</Noleggio>
            <Cambia>CAMBIA L’OFFERTA</Cambia>
            <img src={probs.data.img}></img>
            <Line></Line>
            <Name>{probs.data.name}</Name>
            <PriceMounth>€ {probs.data.per_mounth}/mese I.E.</PriceMounth>
            <Characteristics>
                <div className="row">
                    <div className="text-left col">Anticipo</div>
                    <div className="text-right col">€{probs.data.anticipo}</div>
                </div>
                <GreyLine></GreyLine>
                <div className="row">
                    <div className="text-left col">Durata</div>
                    <div className="text-right col">{probs.data.durata} mesi</div>
                </div>
                <GreyLine></GreyLine>
                <div className="row">
                    <div className="text-left col">Km inc</div>
                    <div className="text-right col">{probs.data.km_inc}km</div>
                </div>
            </Characteristics>
        </StyledCard>
    </div>
    </>
    );
}

function CartRow(){
    const testing_date = [
       {id:1, term:"lungo", img:"images/cart_car1.png", name:"Toyota Auris Hibrid 1.0 Touring sport", per_mounth:1, anticipo:1, durata:36, km_inc:30000 },
       {id:2, term:"lungo", img:"images/cart_car1.png", name:"Toyota Auris Hibrid 1.0 Touring sport", per_mounth:1, anticipo:1, durata:36, km_inc:30000 },
       {id:3, term:"lungo", img:"images/cart_car1.png", name:"Toyota Auris Hibrid 1.0 Touring sport", per_mounth:1, anticipo:1, durata:36, km_inc:30000 },
       {id:4, term:"lungo", img:"images/cart_car1.png", name:"Toyota Auris Hibrid 1.0 Touring sport", per_mounth:1, anticipo:1, durata:36, km_inc:30000 },
       {id:5, term:"lungo", img:"images/cart_car1.png", name:"Toyota Auris Hibrid 1.0 Touring sport", per_mounth:1, anticipo:1, durata:36, km_inc:30000 },
       {id:6, term:"lungo", img:"images/cart_car1.png", name:"Toyota Auris Hibrid 1.0 Touring sport", per_mounth:1, anticipo:1, durata:36, km_inc:30000 },
       {id:7, term:"lungo", img:"images/cart_car1.png", name:"Toyota Auris Hibrid 1.0 Touring sport", per_mounth:1, anticipo:1, durata:36, km_inc:30000 },
       {id:8, term:"lungo", img:"images/cart_car1.png", name:"Toyota Auris Hibrid 1.0 Touring sport", per_mounth:1, anticipo:1, durata:36, km_inc:30000 },
       {id:9, term:"lungo", img:"images/cart_car1.png", name:"Toyota Auris Hibrid 1.0 Touring sport", per_mounth:1, anticipo:1, durata:36, km_inc:30000 },
    ]
    return(
        <>
        <Row className="col-sm-8 row mx-auto text-center">  
        {testing_date.map((post) => {
            /*
            if(post.id%3==1)
            {
                <Row className="col-sm-8 row mx-auto text-center"></Row>;
            }
            */
            <Cart data={post} id={post.id}></Cart>
            /*if(post.id%3==0)
             {
                <Row className="col-sm-8 row mx-auto text-center"></Row>;
            }
            */
            })
        }
        </Row>
        </>
        );
}
export {CartRow};
