import React from "react";
import { Link } from "react-router-dom";
import styled from 'styled-components';
const StyledCard = styled.button`
    background: #F1EBE3;
    margin-bottom: 50px;
    font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Arial, sans-serif;
    font-style: normal;
    padding-bottom: 7px;
    width: 350px;
    position: relative;
`;
const Line = styled.div`
    background: #8D120C;
    height: 10px;
    margin: 0 20px;
`;
const Noleggio = styled.div`
    font-weight: bold;
    font-size: 14px;
    text-transform: uppercase;
    text-align: left!important;
    color: #000;
    padding-left: 20px;
    padding-top: 8px;
`;
const Row = styled.div`
    @media only all and (max-width: 1440px) {
        max-width: 100%!important;
        flex: 0 0 100%!important;
    }
`;
const Cambia = styled.div`
    font-weight: bold;
    font-size: 17px;
    letter-spacing: 0.05em;
    color: #FFFFFF;
    text-align: right!important;
    background: #8D120C;
    border-radius: 19px;
    position: absolute;
    right: -15px;
    top: -15px;
    z-index: 0;
    padding: 10px;
`;
const Name = styled.div`
    font-weight: bold;
    font-size: 14px;
    letter-spacing: 0.05em;
    text-transform: capitalize;
    color: #000000;
    text-align: left!important;
    margin: 8px 20px;
    height: 42px;
`;
const PriceMounth = styled.div`
    font-weight: normal;
    font-size: 24px;
    letter-spacing: 0.05em;
    text-transform: capitalize;
    color: #01579B;
    text-align: right!important;
    margin-right: 20px;
`;
const Characteristics = styled.div`
    font-weight: normal;
    font-size: 14px;
    text-transform: capitalize;
    color: #828282;
    margin: 3px 20px 0 20px;
    position: relative;

`;
const GreyLine = styled.div`
    border: 1px solid #BDBDBD;
    height: 0px;
    margin-bottom: 5px;

`;
const CarWrap = styled.div`
    position: relative;
    width: 340px;
    height: 220px;
    overflow: hidden;
    margin-right: 5px;
    margin-left: 5px;
`;
const Car = styled.img`
    width: 100%;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
`;
function Cart(probs) {
    return(
    <>
    <div className="col-sm-4" >
        <Link to="/Contatti">
            <StyledCard className="mx-auto">
                <Noleggio>noleggio {probs.data.term}<br></br> TERMINE</Noleggio>
                <Cambia>CAMBIA L’OFFERTA</Cambia>
                <CarWrap>
                    <Car src={probs.data.img}></Car>
                </CarWrap>
                <Line></Line>
                <Name>{probs.data.name}</Name>
                <PriceMounth>€ {probs.data.per_mounth}/mese I.E.</PriceMounth>
                <Characteristics>
                    <div className="row">
                        <div className="text-left col p-0">Anticipo</div>
                        <div className="text-right col p-0">€{probs.data.anticipo}</div>
                    </div>
                    <GreyLine></GreyLine>
                    <div className="row">
                        <div className="text-left col p-0">Durata</div>
                        <div className="text-right col p-0">{probs.data.durata} mesi</div>
                    </div>
                    <GreyLine></GreyLine>
                    <div className="row">
                        <div className="text-left col p-0">Km inc</div>
                        <div className="text-right col p-0">{probs.data.km_inc}km</div>
                    </div>
                </Characteristics>
            </StyledCard>
        </Link>
    </div>
    </>
    );
}

function CartRow(probs){

    return(
        <>
        <Row className="col-sm-8 row mx-auto text-center">  
        {probs.date.map((post) => 
            <Cart data={post} key={post.id}></Cart>
            )
        }
        </Row>
        </>
        );
}
export {CartRow};
