import React, { useState, useEffect } from "react";
import styled, {css} from 'styled-components';
import { useParams } from "react-router-dom";
import Form from "./form";
import CartRow from './cart';
const Back = styled.div`
    width: 100%;
    height: 100%;
    background: #F1EBE3;
    margin-top: -50px;
`;
const Container = styled.div`
    font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Arial, sans-serif;
    color: #000000;
    text-align: center;
    padding-bottom: 60px;
    padding-top: 50px;
    @media only all and (max-width: 570px) {
        margin-top: 95px;
    }
    flex: 0 0 70%;
    max-width: 70%;
    position: relative;
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    margin-left: auto;
    margin-right: auto;    
    @media only all and (max-width: 1680px) {
        max-width: 80%!important;
        flex: 0 0 80%!important;
    }
    @media only all and (max-width: 1440px) {
    	max-width: 100%!important;
    	flex: 0 0 100%!important;
    }
`;
const ColonOne = styled.div`
    flex: 0 0 41.666667%;
    max-width: 41.666667%;
    position: relative;
    width: 100%;
    padding-right: 40px;
    margin-top: 150px;
    @media only all and (max-width: 570px) {
         max-width: 100%!important;
        flex: 0 0 100%!important;
        padding-right: 20px;
        padding-left: 20px;
        margin-top: 0px;
    }
`;
const ColonTwo = styled.div`
    flex: 0 0 58.333333%;
    max-width: 58.333333%;
    position: relative;
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    padding-left: 10px;
    padding-right: 10px;
    @media only all and (max-width: 570px) {
        max-width: 100%!important;
        flex: 0 0 100%!important;
        padding-left: 20px;
        padding-right: 20px;
        padding-top: 40px;
    }
`;
const Cambia = styled.div`
    font-weight: bold;
    font-size: 18px;
    letter-spacing: 0.05em;
    color: #01579B;
    text-align: right!important;
    background: #DAA520;
    border-radius: 45px;
    padding-top: 30px;
    padding-right: 40px;
    padding-bottom: 30px;
    padding-left: 40px;
    margin-bottom: 15px;
    margin-right: -20px;
    @media only all and (max-width: 570px) {
        margin-top: 20px;
    }
`;
const CarWrap = styled.div`
    height: 317px;
    overflow: hidden;
    width: 100%;
    position: relative;
`;
const Car = styled.img`
    width: 100%;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
`;
const Line = styled.div`
    background: #8D120C;
    height: 10px;
    margin-left: 80px;
    @media only all and (max-width: 570px) {
        margin-left: 0px;
    }
`;
const CarName = styled.div`
    font-size: 24px;
    margin-right: 10px;
    font-weight: 700;
    @media only all and (max-width: 570px) {
        font-size: 20px;
    }
`;
const BlockCambia = styled.div`
    display: flex;
    width: 100%;
    justify-content: end;
    @media only all and (max-width: 570px) {
        justify-content: center;
    }
`;
const Section = styled.div`
    color: #000000;
    text-align: center;
    margin-left: auto;
    margin-right: auto;
    @media only all and (max-width: 570px) {
        display: none;
        &{
            display: none;
        }
    }
`;
const TitleStyle = styled.div`
    font-size: 40px;
    text-transform: uppercase;
    font-family: 'Poppins', sans-serif;
    color: #8D120C;
    font-weight: 900;
    @media only all and (max-width: 570px) {
        font-size: 40px;
    }
`;
const Subtitle = styled.div`
    font-size: 14px;
    & > span{
        color: #8D120C; 
    }
    @media only all and (max-width: 570px) {
        font-size: 18px;
    }
`;
const Label = styled.div`
    font-size: 18px;
    text-transform: uppercase;
    font-weight: 700;
    color: #000000;
    background: #FFD700;
    border-radius: 10px;
    padding-left: 60px;
    padding-right: 60px;
    padding-bottom: 15px;
    padding-top: 15px;
    margin-top: 30px;
    margin-bottom: 20px;
    @media only all and (max-width: 570px) {
        background: #DAA520;
    }
`;
const FourSquares = styled.div`
    width: 100%;
    display: flex;
    flex-wrap: wrap;

`;
const Square = styled.div`
    flex: 0 0 50%;
    max-width: 50%;
    position: relative;
    padding: 15px;
    & > div{
        height: 230px;
        border-radius: 50px;
    }
    ${props => props.one && css`
        & > div{
            background: #FF7A00;
        }  
    `}
    ${props => props.two && css`
        & > div{
            background: #84D268;  
        } 
    `}
    ${props => props.tree && css`
        & > div{
            background: #8BC5F3; 
        }
    `}
    ${props => props.four && css`
        & > div{
            background: #DAA520;
        } 
    `}
    @media only all and (max-width: 570px) {
        flex: 0 0 100%;
        max-width: 100%;
    }
`;
const CambiaAnticipo = styled.div`
    width: 100%;
    background: #8D120C;
    border-radius: 50px;
    height: 140px;
    margin-top: 25px;
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    @media only all and (max-width: 570px) {
        margin-top: 25px;
        height: auto;
        display: block;
    }
`;
const AnticipoText = styled.div`
    font-weight: 700;
    font-size: 30px;
    color: #FFFFFF;
    -ms-flex-preferred-size: 0;
    flex-basis: 0;
    -ms-flex-positive: 1;
    flex-grow: 1;
    max-width: 100%;
    margin-top: 45px;

`;
const CambiaButton = styled.button`
    font-weight: 700;
    font-size: 44px;
    color: #FFFFFF;
    width: 350px;
    height: 100px;
    background: #1D1D1B;
    border-radius: 50px;
    -ms-flex-preferred-size: 0;
    flex-basis: 0;
    ms-flex-positive: 1;
    flex-grow: 1;
    max-width: 100%;
    margin-top: 20px;
    margin-right: 10px;
    & > img{
        width: 60px;
        height: 60px;
    }
    @media only all and (max-width: 570px) {
        margin-right: 0px;
        margin-bottom: 30px;
    }
`;
const Characteristics = styled.div`
    font-size: 21px;
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    margin-top: 5px;
    & > img {
        -ms-flex-preferred-size: 0;
        flex-basis: 0;
        -ms-flex-positive: 1;
        flex-grow: 1;
        max-width: 100%;
        width: 60px;
        height: 60px;
    }
    & > div {
        -ms-flex-preferred-size: 0;
        flex-basis: 0;
        -ms-flex-positive: 1;
        flex-grow: 1;
        max-width: 100%;
        margin-top: 15px;
        margin-bottom: 10px;
        margin-left: 5px;
        margin-right: 5px;
    }
    @media only all and (max-width: 570px) {
        font-size: 18px;
        & > img {
            width: 40px;
            height: 40px;
        }
        & > div {
            margin-top: 5px;
        }
    }
    @media only all and (max-width: 400px) {
        font-size: 15px;
        & > div {
            margin-top: 10px;
            margin-left: 3px;
            margin-right: 3px;
        }
    }

`;
const Check = styled.div`
    font-size: 24px;
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    margin-left: 50px;
    margin-top: 10px;
    & > div{
        width: 100%;
        display: flex;
        flex-wrap: wrap;
        margin-top: 15px;
    }
    & > div > div {
        margin-left 15px;
    }
    @media only all and (max-width: 570px) {
        font-size: 21px;
        margin-left: 20px;
    }
`;
const ShadowBlock = styled.div`   
    background: #EDE7DF;
    border-radius: 10px;
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    padding-bottom: 40px;
    padding-top: 20px;

`;
const ShadowBlock2 = styled.div`
    background: #EDE7DF;
    border-radius: 10px;
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    padding-bottom: 30px;
    padding-left: 15px;
    padding-right: 15px;
    @media only all and (max-width: 570px) {
        border-radius: 0px;
        box-shadow: none;
        background: rgb(241, 235, 227) none repeat scroll 0% 0%;
        padding-left: 0px;
        padding-right: 0px;
    }
`;
const BlockInfo = styled.div`
    font-size: 24px;
    line-height: 150%;
    text-align: center;
    color: #4F4F4F;
    margin-top: 30px;
    & > div{
        margin-left: 170px;
        margin-right: 170px;
    }
