import React from "react";
import {Title} from './title';
import styled, { css } from 'styled-components';
import {VantaggiNoleggio} from './vantaggi_noleggio';
import { Link } from "react-router-dom";
import { useEffect, useRef } from "react";
import { useLocation } from "react-router-dom";


const Container = styled.div`
    font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Arial, sans-serif;
    @media only all and (max-width: 1440px) {
        max-width: 100%!important;
        flex: 0 0 100%!important;
    }
    @media only all and (max-width: 500px) {
        padding-left: 20px;
        padding-right: 20px;
    }
`;
const RedRectangle = styled.div`
    height: 264px;
    background: #8D120C;
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    border-radius: 20px;
    position: relative;
    @media only all and (max-width: 500px) {
        padding-left: 40px;
        padding-right: 40px;
    }
`;
const RedTitle = styled.div`
    font-family: Poppins;
    font-style: normal;
    font-weight: bold;
    font-size: 48px;
    text-transform: uppercase;
    color: #FFFFFF;
    padding-top: 60px;
    @media only all and (max-width: 500px) {
        font-size: 24px;
        padding-top: 30px;
    }
`;
const RedText = styled.div`
    font-family: Poppins;
    font-style: normal;
    font-weight: bold;
    font-size: 40px;
    text-transform: uppercase;
    color: #FFFFFF;
    padding-top: 40px;
    @media only all and (max-width: 500px) {
        font-size: 24px;
    }
`;
const MultCar = styled.img`
    @media only all and (max-width: 500px) {
        width: 340px;
        height: 107px;
    }
`;
const Logo = styled.img`
    position: absolute;
    right: -1.5%;
    top: -35%;
    @media only all and (max-width: 500px) {
        width: 80px;
        height: 91px;
        right: -5%;
        top: -15%;
    }
`;
const BlackSquare = styled.div`
    font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Arial, sans-serif;
    position: relative;
    height: 601px;
    background: #1D1D1B;
    border-radius: 20px;
    margin-top: 40px;
    margin-bottom: 24px;
`;
const BlackText = styled.div`
    font-weight: bold;
    font-size: 32px;
    line-height: 42px;
    text-transform: uppercase;
    color: #FFFFFF;
    position: relative;
    z-index: 2;
    ${props => props.one && css`
        right: 145px;
        top: 80px;
    `}
    ${props => props.two && css`
        right: -20px;
        top: 160px;
    `}
    ${props => props.tree && css`
        right: 40px;
        top: 215px;
    `}
    ${props => props.four && css`
        top: 265px;
        left: 125px;
    `}
`;
const CreditCard = styled.img`
    position: absolute;
    left: -13%;
    top: -26%;
    @media only all and (max-width: 1440px) {
        left: -28%;
    }
`;
const Like = styled.img`
    position: absolute;
    right: -10%;
    top: -23%;
    @media only all and (max-width: 1440px) {
        right: -30%;
    }
`;
const CarVendo = styled.img`
    position: absolute;
    left: -10%;
    bottom: -18%;
    @media only all and (max-width: 1440px) {
        left: -27%;
    }

`;
const Circle = styled.div`
    position: absolute;
    width: 100px;
    height: 100px;
    background: #8D120C;
    border-radius: 100px;
    ${props => props.one && css`
        left: 120px;
        bottom: 230px;
        @media only all and (max-width: 1440px) {
            left: 10px;
        }
    `}
    ${props => props.two && css`
        right: 85px;
        top: 170px;
        @media only all and (max-width: 1440px) {
            right: 10px;
        }
    `}
    ${props => props.tree && css`
        right: 85px;
        bottom: 85px;
        @media only all and (max-width: 1440px) {
            right: 10px;
        }
    `}

`;
const RedArrowContainer = styled.div`
    position: relative;
    height: 80px;
`;
const RedArrow = styled.button`
    position: absolute;
    top: 0;
    right: 0;
    width: 540px;
    height: 78px;
    background: #8D120C;
    border-radius: 38.5px;
    padding-top: 10px;
    padding-bottom: 10px;
    padding-left: 35px;
    padding-right: 10px;
`;
const Contatto = styled.div`
    text-decoration-line: underline;
    text-transform: uppercase;
    font-weight: 700;
    font-size: 32px;
    padding-left: 0;
    padding-right: 0;
    padding-top: 5px;
    color: #FFFFFF;
`;
const BlackArrow = styled.img`
    width: 57px;
    height: 57px;
    background: #1D1D1B;
    border-radius: 40px;
    margin: 0 10px 10px 15px;
`;
const TitleGuarda = styled.div`
    font-weight: bold;
    font-size: 34px;
    line-height: 41px;
    text-transform: uppercase;
    color: #FFFFFF;
    margin-top: 40px;
    margin-bottom: 24px;
`;

const BlackRectangle = styled.div`
    position: absolute;
    top: 0;
    left: -400px;
    background: #1D1D1B;
    width: 1230px;
    height: 446px;
    border-radius: 20px;
    z-index: 0;
`;
const Video = styled.iframe`
    text-align: right;
    position: relative;
    z-index: 1;
    border: none;
    outline: none;
    margin-top: 24px;
`;
const InfoTitle = styled.div`
    font-weight: bold;
    font-size: 20px;
    line-height: 24px;
    text-transform: uppercase;
    color: #FFFFFF;
    background: #1D1D1B;
    border-radius: 20px;
    margin-left: 50px;
    padding-top: 25px;
    padding-bottom: 25px;
`;
const InfoTextBox = styled.div`
    background: #1D1D1B;
    border-radius: 20px;
    font-weight: normal;
    font-size: 20px;
    color: #FFFFFF;
    text-align: left;
    padding-left: 10px;
    padding-bottom: 20px;
    margin-top: 15px;
    margin-left: 50px;
`;
const SmallIcon = styled.img`
    width: 30px;
    height: 30px;
    margin-right: 10px;  
    ${props => props.one && css`
        margin-top: 15px;
    `}
`;
const InfoText = styled.div`
    padding-top: 40px;
    ${props => props.one && css`
        margin-left: 38px;
    `}
`;
const BigText = styled.div`
    font-style: italic;
    font-weight: normal;
    font-size: 30px;
    line-height: 66px;
    text-align: left;
    & > div{
        margin-top: 60px;
    }
`;
const BigCircle = styled.div`
    {/*Pureshka252203$*/}
    position: absolute;
    width: 570px;
    height: 570px;
    background: #8D120C;
    border-radius: 700px;
    ${props => props.one && css`
        right: -600px;
        top: 710px;
    `}
    ${props => props.two && css`
        left: -600px;
        top: 1300px;
    `}
    ${props => props.tree && css`
        right: -600px;
        top: 2000px;
    `}

`;
function Vendo_compro_auto() {
    const testRef = useRef(null);
    const scrollToElement = () => testRef.current.scrollIntoView();
    return(
        <>
        <Title/>
        <Container className="col-sm-8 mx-auto text-center"> 
        <MultCar src="./static/images/mult_car.png"></MultCar>
        <RedRectangle> 
            <Logo src="./static/images/logo4.svg"></Logo>
            <RedTitle>
                Perchè vendere la tua auto a noi?
            </RedTitle>
            <RedText>
                Perchè conviene!
            </RedText>
        </RedRectangle>       
        </Container>
        <div className="col-sm-8 mx-auto text-center">
            <BlackSquare>
                <CreditCard src="./static/images/credit_card.png"></CreditCard>
                <Like src="./static/images/like.png"></Like>
                <BlackText one>Pagamento immediato</BlackText>
                <BlackText two>Passaggio di proprieta immediato</BlackText>
                <BlackText tree>Valore quotazione dichiarata da<br></br> Quatroruote</BlackText>
                <BlackText four>Garanzia di sicurezza e<br></br> PROFESSIONALITÀ</BlackText>
                <CarVendo src="./static/images/car_vendo.png"></CarVendo>
                <Circle one></Circle>
                <Circle two></Circle>
                <Circle tree></Circle>
            </BlackSquare>
            <RedArrowContainer>
                <RedArrow className="row" onClick={scrollToElement}>
                    <SmallIcon src="/static/images/whatsapp.svg" one></SmallIcon>
                    <Contatto className="col-sm">CONTATTO IMMEDIATO</Contatto>
                    <BlackArrow src="/static/images/Arrow_bottom.svg"></BlackArrow>
                </RedArrow>
            </RedArrowContainer>
        </div>
        <Container className="col-sm-8 mx-auto text-center row"> 
        <TitleGuarda className="mx-auto">guarda il video in modo da non dover leggere</TitleGuarda>

        <div className="col-sm-8">
        <BlackRectangle>
        </BlackRectangle>
        <Video width="700" height="400" src="https://www.youtube-nocookie.com/embed/J-BHTW9USe0" title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></Video>
        </div>
        <div className="col-sm-4">
            <InfoTitle>VENDITA & ACQUISTO AUTO www.tanteauto.it</InfoTitle>
            <InfoTextBox>
            <InfoText one>Fabrizio Palpon</InfoText>
            <InfoText><SmallIcon src="/static/images/whatsapp.svg"></SmallIcon>393 989 39 09</InfoText>
            <InfoText><SmallIcon src="/static/images/mail-red.png"></SmallIcon>fabrizio@tanteauto.it</InfoText>
            <InfoText className="row">
                <SmallIcon src="/static/images/location.svg"></SmallIcon>
                <div className="col-sm p-0">via Montorfano<br></br> 74/76 Seregno</div>
            </InfoText>
            </InfoTextBox>
        </div>
        <BigText>
            <div>Guarda che non leggerai le solite cose tipo noi facciamo il raviolo quadrato dal 1921 e mio nonno ha brevettato la mutanda invisibile e altre cavolate simili, se leggerai queste 4 righe conoscerai la vera storia di….</div>
            <div>Iniziamo Questa è la storia di uno di noi nato per caso in via Gluck, in una casa fuori città gente tranquilla che lavorava….. cantava Adriano Celentano.</div>
            <div>La mia canzone ha nel testo anche “mangiava con Quattroruote e conosceva tutte le auto….”, uno che voleva fare lo psicologo è invece è stato divorato dalla passione</div>
            <div>Così da una passione troppo forte per i motori lo studio era in secondo piano, quindi al lavoro di giorno e a scuola di sera, una volta si usava cosi…</div>
            <div>E mi divertivo così tanto che alla fine ho aperto un business proprio nella manutenzione delle auto, poi altri due e poi uno che si occupa solo di vendita auto.</div>
            <div>Quindi apro la mia prima società nel campo della manutenzione delle auto nel 1989, poi nel 1999 nuova sede, nel 2006 la seconda società, nel 2009 inizio a vendere auto nella prima società, nel 2014 apro un autosalone, sempre a Seregno, nel 2017 inizio la vendita On Line con anche consegna a casa del cliente, isole comprese, e poi avrei un altro progetto in cantiere ma vorrei farti una sorpresa più avanti :-D</div>
            <div>Ma a parte quello che ho fatto, il motivo per cui l’ho fatto e lo faccio è perché mi diverte tanto il mio lavoro. </div>
            <div>Mi piace il contatto con le persone oneste e corrette, gentili e intelligenti, se poi hanno anche passione per le auto è il massimo, ma devo dire che a volte gli incontri più belli sono quelli con le persone che vivono l’auto come mezzo di spostamento, e cosi si parla di vita e di esperienze reciproche.</div>
            <div>E allora se tu stai leggendo queste righe significa solo due cose, o sei solo curioso oppure hai il problema di dovere vendere la tua auto. </div>
            <div>E se hai un problema, ti do una bella notizia… io ho la soluzione! Ti compro la tua macchina e tac , il problema scompare grazie a <a href="https://quicomproauto.it/" target="_blank" >www.quicomproauto.it</a></div>
            <div>Quindi Se vuoi essere felice dopo averla venduta devi venderla a me! Questo è il mantra che ripeto a tutte le persone con cui entro in contatto, perché faccio il mio lavoro con grande amore e passione e onesta, e vedere felici i miei clienti è il motivo per cui mi piace fare il mio lavoro, e cosi la passione si alimenta ogni giorno 😊 
            <br ref={testRef}></br>Quindi non indugiare, affidati ad un professionista serio e onesto cosi sarai felice anche tu! Con affetto, stima simpatia e tanti auguri di una vita felice, Fabrizio Palpon.    
            </div>
            <BigCircle one></BigCircle>
            <BigCircle two></BigCircle>
            <BigCircle tree ></BigCircle>
        </BigText>
        </Container>
        <VantaggiNoleggio />
        </>
    );
}
export {Vendo_compro_auto};
