import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import styled, {css} from 'styled-components';
import { useParams } from "react-router-dom";
const Back = styled.div`
    width: 100%;
    height: 100%;
    background: #F1EBE3;
    margin-top: -50px;
`;
const Container = styled.div`
    font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Arial, sans-serif;
    color: #000000;
    text-align: center;
    padding-bottom: 60px;
    padding-top: 50px;
    @media only all and (max-width: 570px) {
        margin-top: 95px;
    }
    flex: 0 0 66.666667%;
    max-width: 66.666667%;
    position: relative;
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    margin-left: auto;
    margin-right: auto;    
    @media only all and (max-width: 1680px) {
        max-width: 80%!important;
        flex: 0 0 80%!important;
    }
    @media only all and (max-width: 1440px) {
    	max-width: 100%!important;
    	flex: 0 0 100%!important;
    }
`;
const Colon = styled.div`
    flex: 0 0 50%;
    max-width: 50%;
    position: relative;
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    padding-left: 10px;
    padding-right: 10px;
    media only all and (max-width: 1200px) {
        max-width: 100%!important;
        flex: 0 0 100%!important;
    }
    ${props => props.one && css`
        margin-top: 95px;
    `}
`;
const BlockCambia = styled.div`
    display: flex;
    width: 100%;
    justify-content: end;
`;
const Cambia = styled.div`
    font-weight: bold;
    font-size: 18px;
    letter-spacing: 0.05em;
    color: #01579B;
    text-align: right!important;
    background: #DAA520;
    border-radius: 45px;
    padding: 30px;
    margin-bottom: 15px;
    @media only all and (max-width: 570px) {
        font-size: 14px;
    }
`;
const CarWrap = styled.div`
    height: 310px;
    overflow: hidden;
    margin-right: 20px;
    margin-left: 20px;
    width: 100%;
    position: relative;
`;
const Car = styled.img`
    width: 100%;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
`;
const Line = styled.div`
    background: #8D120C;
    width: 100%;
    height: 10px;
    margin: 20px 40px 0 40px;
`;
const CarName = styled.div`
    font-size: 24px;
    margin-right: 40px;
`;
const Section = styled.div`
    color: #000000;
    text-align: center;
    margin-left: auto;
    margin-right: auto;
    @media only all and (max-width: 570px) {
        margin-left: 20px;
        margin-right: 20px;
        margin-top: 95px;
    }
`;
const TitleStyle = styled.div`
    font-size: 40px;
    text-transform: uppercase;
    font-family: 'Poppins', sans-serif;
    color: #8D120C;
    font-weight: 900;
    @media only all and (max-width: 570px) {
        font-size: 40px;
    }
`;
const Subtitle = styled.div`
    font-size: 14px;
    & > span{
        color: #8D120C; 
    }
    @media only all and (max-width: 570px) {
        font-size: 18px;
    }
`;
const Label = styled.div`
    font-size: 18px;
    text-transform: uppercase;
    font-weight: 700;
    color: #000000;
    background: #FFD700;
    border-radius: 10px;
    padding-left: 60px;
    padding-right: 60px;
    padding-bottom: 15px;
    padding-top: 15px;
`;

export default function Details_Long() {
    const { id } = useParams();
    const [dataset, setData] = useState([]);
    async function getData() {
        try {
            const res = await fetch(`/api/car/lungo/details/${id}/`);
            const data = await res.json();
            data.map((post) => setData(post));
        } catch (e) {
            console.log(e);
        }
    }
    useEffect(() => {
        getData();
    }, []);
    return(
        <>
        <Back>
            <Container>
                <Colon one>
                    <BlockCambia>
                        <Cambia>CAMBIA Lâ€™OFFERTA</Cambia>
                    </BlockCambia>
                    <CarWrap>
                        <Car src={dataset.foto}></Car>
                    </CarWrap>
                    <Line></Line>
                     <BlockCambia>
                        <CarName>{ dataset.nome }</CarName>
                    </BlockCambia>
                </Colon>
                <Colon>
                    <Section>
                        <TitleStyle>Palponauto</TitleStyle>
                        <Subtitle>noleggio <span>lungo</span> e <span>breve</span> termine</Subtitle>
                        <Label>noleggio LUNGO TERMINE</Label>
                    </Section>

                </Colon>
            </Container>
        </Back>
        </>
    );
}
