import React, { useState, useEffect } from "react";
import Title from './title';
import styled, { css } from 'styled-components';
import CartRow from './cart';
import { Link } from "react-router-dom";

const RedRectangle = styled.div`
    background: #81100B;
    border-radius: 10px;
    text-align: center;
    padding: 41px 36px;
    margin-top: 72px;
    margin-bottom: 32px;
`;
const Text = styled.div`
    font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Arial, sans-serif;
    font-style: normal;
    font-weight: normal;
    font-size: 28px;
    letter-spacing: 0.05em;
    color: #FFFFFF;
    padding-bottom: 50px;
    ${props => props.none && css`
        padding-bottom: 0px;
    `}
    @media only all and (max-width: 570px) {
        font-size: 24px;
    }
`;
const Logo = styled.img`
    position: absolute;
    right: -4.3%;
    top: -7%;
    z-index: 1;
    text-align: right;
    @media only all and (max-width: 1680px) {
        right: 0%;
        top: -7%;
    }
    @media only all and (max-width: 570px) {
        width: 88px;
        height: 100px;
        right: -1%;
        top: 0%;
    }
`;
const BigButton = styled.button`
    background: #DAA520;
    font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Arial, sans-serif;
    font-style: normal;
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    border-radius: 10px;
    border: none;
    outline: none;
    height: 161px;
    margin-bottom: 56px;
    width: 100%;
    ${props => props.one && css`
        margin-right: 30px;
    `}
    @media only all and (max-width: 570px) {
        margin-bottom: 30px;
        margin-right: 0px;
    }
`;
const TitleButton = styled.div`
    text-align: center;
    font-weight: bold;
    font-size: 32px;
    line-height: 38px;
    letter-spacing: 0.05em;
    text-transform: uppercase;
    color: #000000;
    @media only all and (max-width: 570px) {
        font-size: 24px;
    }
    ${props => props.pad && css`
        padding-top: 38px;
        @media only all and (max-width: 570px) {
            @media only all and (min-width: 355px) {
                margin-bottom: 25px;
                margin-top: 15px;
            }
        }
    `}
    ${props => props.two && css`
        @media only all and (max-width: 570px) {
            @media only all and (min-width: 344px) {
                margin-bottom: 10px;
                margin-top: 30px;
            }
        }
    `}
`;
const Scopri = styled.div`
    text-align: right;
    text-decoration-line: underline;
    text-transform: uppercase;
    font-weight: bold;
    font-size: 24px;
    position: relative;
    bottom: -20px;
    color: #000000;
    @media only all and (max-width: 570px) {
        bottom: 0px;
    }
`;
const Arrow = styled.img`
    text-align: right;
    margin-left: 5px;
    @media only all and (max-width: 570px) {
        width: 21px;
        height: 30px;
        margin-bottom: 3px;
    }
`;
const Button = styled.button`
    width: 100%;
    font-style: normal;
    font-weight: bold;
    font-size: 17px;
    text-transform: uppercase;
    border-radius: 10px;
    border: none;
    outline: none;
    background: none;
    margin-top: -6px;
    background: #DAA520;
    color: #000000;
    text-align: left;
    padding-left: 15px;
    margin-bottom: 50px;
    @media only all and (max-width: 1680px) {
        margin-top: 0px;
    }
    @media only all and (max-width: 570px) {
        margin-bottom: 30px;
        @media only all and (min-width: 401px) {
           margin-top: 0px;
        }
    }
`;
const Container = styled.div`
    @media only all and (max-width: 1680px) {
        max-width: 80%!important;
        flex: 0 0 80%!important;
    }
    @media only all and (max-width: 1440px) {
        max-width: 100%!important;
        flex: 0 0 100%!important;
    }
    ${props => props.mar && css`
        @media only all and (max-width: 570px) {
            margin-bottom: 50px;
        }
    `}
`;
const CarWrap = styled.div`
    position: relative;
    width: 100%;
    height: 234px;
    overflow: hidden;
    border-radius: 5px;
`;
const Car = styled.img`
    width: 100%;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
`;
const ContainerPadding = styled.div`
    @media only all and (max-width: 570px) {
        padding-left: 0;
        padding-right: 0;
    }
`;
export default function Long_Rent_Benefits() {
    const [dates, setData] = useState([]);
    async function componentDidMount() {
        try {
            const res = await fetch('/api/offerte/lungo');
            const data = await res.json();
            setData(data);
        } catch (e) {
            console.log(e);
        }
    }
    useEffect(() => {
        componentDidMount();
    }, []);
    return(
        <>
        <Title/>
        <div className="row">
            <Container className="col-sm-8 mx-auto">
                <RedRectangle >
                    <Logo src="/static/images/logo4.svg"></Logo>
                    <Text>Scegli l’innovazione, guida la tua prossima auto senza fatica e senza pensieri</Text>
                    <Text>Elettrica, ibrida, benzina o diesel scegli tu come migliorare il tuo stile di vita</Text>
                    <Text>Fai un investimento che ti fa risparmiare e ti dà risultati immediat</Text>
                    <Text none>Aumenta la tua sicurezza finanziaria, scegli il noleggio a lungo termine</Text>
                </RedRectangle>
            </Container>
            <Container className="col-sm-8 mx-auto row">
                <ContainerPadding className="col-sm-6">
                    <Link to="/Privati_Long">
                        <BigButton one>
                            <TitleButton pad>Vantaggi per i privati</TitleButton>
                            <Scopri>Scopri di più<Arrow src="/static/images/arrow3.svg"></Arrow></Scopri>
                        </BigButton>
                    </Link>
                </ContainerPadding>
                <ContainerPadding className="col-sm-6">
                    <Link to="/Vantaggi_Long">
                        <BigButton>
                            <TitleButton two>Vantaggi per aziende e partita IVA</TitleButton>
                            <Scopri>Scopri di più<Arrow src="/static/images/arrow3.svg"></Arrow></Scopri>
                        </BigButton>
                    </Link>
                </ContainerPadding>
            </Container>
        </div>
        <Container className="row col-sm-8 mx-auto row text-center" mar>
            <ContainerPadding className="col-sm-4">
                <div className="mx-auto"> 
                    <Link to="/Contatti">
                        <CarWrap>
                            <Car src="/static/images/Rectangle_94.png"></Car>
                        </CarWrap>
                        <Button>offerte<br></br> noleggio furgoni LUNGO TERMINE</Button>
                    </Link>
                </div> 
            </ContainerPadding>
            <ContainerPadding className="col-sm-4">  
                <div className="mx-auto">
                    <Link to="/car_long_term">
                        <CarWrap>
                            <Car src="/static/images/Rectangle_93.png"></Car>
                        </CarWrap>
                        <Button>offerte<br></br> noleggio auto LUNGO TERMINE</Button>
                    </Link>
                </div> 
            </ContainerPadding>
            <ContainerPadding className="col-sm-4">  
                <div className="mx-auto">
                    <Link to="/Contatti">
                        <CarWrap>
                            <Car src="/static/images/Rectangle_95.png"></Car>
                        </CarWrap>
                        <Button>offerte<br></br> noleggio moto LUNGO TERMINE</Button>
                    </Link>
                </div>
            </ContainerPadding>
        </Container>
        <CartRow date={dates}/>
        </>
    );
}
