import React from "react";
import styled, { css } from 'styled-components';
import {Link} from "react-router-dom";

const RedRectangle = styled.div`
    background: #81100B;
    height: 441px;
    position: absolute;
    z-index: 0;

    ${props => props.one && css`
        width: 1330px;
        left: 17%;
        top: 0px;
        border-radius: 20px 0px 0px 20px;
    `}
    ${props => props.two && css`
        width: 1234px;
        right: 24.1%;
        top: 0px;
        border-radius: 0px 20px 20px 0px;
        @media only all and (min-width: 1920px) {
            right: 29%;
        }
    `}
`;
const ContainerLungo = styled.div`
    position: relative;
    margin-top: 40px;
    height: 441px;
    @media only all and (max-width: 1440px) {
        max-width: 100%!important;
		flex: 0 0 100%!important;
    }
`;
const Logo1 = styled.img`
    z-index: 1;
    position: absolute;
    right: 0%;
    top: -8%;
`;
const Logo2 = styled.img`
    z-index: 1;
    position: absolute;
    left: -2%;
    top: -8%;
    @media only all and (max-width: 1440px) {
        left: 0%;
    }
`;
const CarLungo = styled.img`
    z-index: 1;
    position: absolute;
    left: -5%;
    bottom: -10%;
    @media only all and (max-width: 1440px) {
        left: 0%;
    }
`;
const CarBreve = styled.img`
    z-index: 1;
    position: absolute;
    right: 0%;
    bottom: -14%;
    @media only all and (max-width: 1440px) {
        right: 15%;
    }
`;
const TitleLungo = styled.div`
    z-index: 1;
    position: relative;
    text-align: center;
    letter-spacing: 0.05em;
    text-transform: uppercase;
    font-weight: bold;
    font-size: 38px;
    ${props => props.one && css`
        color: #DAA520;
        margin-left: 23%;
    `}
    ${props => props.two && css`
        margin-left: 6%;
        @media only all and (max-width: 1440px) {
            margin-left: 10%;
        }
    `}
`;
const Text = styled.div`
    z-index: 1;
    position: relative;
    font-size: 30px;
    line-height: 36px;
    width: 800px;
    ${props => props.one && css`
        margin-left: 40%;
        margin-top: -5%;
    `}
    ${props => props.two && css`
        text-align: center;
        margin-left: 50%;
        margin-top: -5%;
        margin-right: 6%;
    `}
    ${props => props.tree && css`
        margin-left: 1%;
        margin-top: 0%;
    `}
    ${props => props.four && css`
        margin-left: 6%;
        margin-top: -4%;
    `}
    ${props => props.five && css`
        margin-left: 1%;
        margin-top: -5%;
    `}
`;
const BigArrow = styled(Link)`
    position: relative;
    top: 0px;
    left: 700px;
    width: 825px;
    height: 77px;
    background: #81100B;
    border-radius: 38.5px;
    text-align: right;
    margin-bottom: 20px;
    margin-top: 25px;
    @media only all and (max-width: 1440px) {
        left: 35%;
    }
    ${props => props.two && css`
        text-align: left;
        left: 325px;
        @media only all and (max-width: 1440px) {
            left: 2%;
        }
    `}
`;
const Arrow = styled.img`
    width: 57px;
    height: 57px;
    background: #1D1D1B;
    border-radius: 40px;
    margin: 10px 20px 10px 0px;
    ${props => props.two && css`
        margin: 10px 0px 10px 20px;
    `}

`;
const TitleArrow = styled.div`
    font-weight: bold;
    font-size: 43px;
    letter-spacing: 0.01em;
    text-decoration-line: underline;
    text-transform: uppercase;
    color: #FFFFFF;
    padding-top: 5px;
`;
const SmallRedRectangle = styled.div`
    position: relative;
    width: 1420px;
    left: 0px;
    box-shadow: 0px 10px 5px rgba(0, 0, 0, 0.25);
    border-radius: 20px;
    background: #81100B;
    margin-top: 50px;
    margin-right: 15px;
    margin-left: 0px;

`;
const TitleRedRectangle = styled.div`
    font-weight: bold;
    font-size: 43px;
    letter-spacing: 0.01em;
    text-transform: uppercase;
    color: #FFFFFF;
    text-align: center;
    margin-left: 250px;
`;
const BlackRectangle = styled.div`
    position: absolute;
    right: 0px;
    width: 1560px;
    height: 310px;
    background: #10100F;
    box-shadow: 0px 10px 5px rgba(0, 0, 0, 0.25);
    border-radius: 10px;

`;
const BlackContainer = styled.div`
    margin-right: 400px;
    margin-left: 360px;
    text-align: center;
    padding-top: 10px;
    flex-wrap: wrap;
    display: flex;
    @media only all and (max-width: 1440px) {
        margin: 0;
    }
`;
const BlackTitle = styled.div`
    font-style: normal;
    font-weight: 300;
    font-size: 24px;
    letter-spacing: 0.05em;
    text-transform: uppercase;
    color: #FFFFFF;
    margin-top: 10px;
`;
const BlackText = styled.div`
    font-style: normal;
    font-weight: 300;
    font-size: 20px;
    letter-spacing: 0.05em;
    color: #FFFFFF;
    margin-top: 30px;
`;
const Black = styled.div`
    height: 310px;
    margin-bottom: 60px;
    margin-top: 22px;
`;
function VantaggiNoleggio() {
    return(
    <>
    <ContainerLungo className="col-sm-8 mx-auto row">
        <RedRectangle one></RedRectangle>
        <Logo1 src="./static/images/logo2.svg"></Logo1>
        <TitleLungo one>VANTAGGI NOLEGGIO LUNGO TERMINE</TitleLungo>
        <Text one>Scegli l’innovazione, guida la tua prossima auto senza fatica e senza pensieri</Text>
        <Text two>Elettrica, ibrida, benzina o diesel scegli tu come migliorare il tuo stile di vita</Text>
        <CarLungo src="./static/images/car_lungo.png"></CarLungo>
    </ContainerLungo>
    <ButtonScopri link={"/Long_Rent_Benefits"}/>
    <ContainerLungo className="col-sm-8 mx-auto row">
        <RedRectangle two></RedRectangle>
        <Logo2 src="./static/images/logo3.svg"></Logo2>
        <TitleLungo two>VANTAGGI NOLEGGIO BREVE TERMINE</TitleLungo>
        <Text tree>Risolve le difficoltà subito</Text>
        <Text four>Noleggia un giorno, un mese, un anno</Text>
        <Text five>Come vuoi quando vuoi tu</Text>
        <CarBreve src="./static/images/car_breve.png"></CarBreve>
    </ContainerLungo>
        <BigArrow two to="/Short_Rent_Benefits" className="row">   
            <Arrow src="./static/images/expand_arrow2.svg" two></Arrow>
            <TitleArrow className="col-sm">SCOPRI L’OFFERTA</TitleArrow>
        </BigArrow>
    <div>
        <SmallRedRectangle>
            <TitleRedRectangle>PERCHE' NOLEGGIARE UNA AUTO E'<br></br> UNA GRANDE SOLUZIONE</TitleRedRectangle>
        </SmallRedRectangle>
    </div>
    <ButtonScopri link={"/Contatti"}/>
    <Black>
        <BlackRectangle>
        </BlackRectangle>
            <BlackContainer>
            <div className="col-sm-4">
                <img src="./static/images/Business.svg"></img>
                <BlackTitle>Per le aziende</BlackTitle>
                <BlackText>Un solo fornitore Una sola unica fattura al mese I costi sono sempre fissi Molti vantaggi fiscali</BlackText>
            </div>
            <div className="col-sm-4">
                <img src="./static/images/User.svg"></img>
                <BlackTitle>Per i privati</BlackTitle>
                <BlackText>Elimina la svalutazione del bene acquistato, costi certi e sicuri ogni mese senza sorprese</BlackText>
            </div>
            <div className="col-sm-4">
                <img src="./static/images/People.svg"></img>
                <BlackTitle>Per tutti</BlackTitle>
                <BlackText>Il noleggio è l’innovazione dello stile di vita, è comodo e facile e ti fa stare meglio</BlackText>
            </div>
            </BlackContainer>

    </Black>
    </>
    );
}
function ButtonScopri(props) {
    return(
    <>
    <BigArrow to={{pathname: props.link}} className="row">
            <TitleArrow className="col-sm">SCOPRI L’OFFERTA</TitleArrow>
            <Arrow src="./static/images/expand_arrow1.svg"></Arrow>
    </BigArrow>
    </>
    );
}
export {VantaggiNoleggio};
