import React, { useState, useRef } from "react";
import Title from './title';
import styled, { css } from 'styled-components';
import { Link } from "react-router-dom";
import VantaggiNoleggio from './vantaggi_noleggio';
import Cookies from 'js-cookie';

const Container = styled.div`
    font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Arial, sans-serif;
    @media only all and (max-width: 1680px) {
        max-width: 80%!important;
        flex: 0 0 80%!important;
    }
    @media only all and (max-width: 1440px) {
        max-width: 100%!important;
        flex: 0 0 100%!important;
    }
    @media only all and (max-width: 570px) {
        padding-left: 20px;
        padding-right: 20px;
    }
`;
const RedRectangle = styled.div`
    height: 264px;
    background: #8D120C;
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    border-radius: 20px;
    position: relative;
    @media only all and (max-width: 570px) {
        padding-left: 45px;
        padding-right: 45px;
        height: auto;
    }
`;
const RedTitle = styled.div`
    font-family: Poppins;
    font-style: normal;
    font-weight: bold;
    font-size: 48px;
    text-transform: uppercase;
    color: #FFFFFF;
    padding-top: 60px;
    @media only all and (max-width: 570px) {
        font-size: 24px;
        padding-top: 30px;
    }
`;
const RedText = styled.div`
    font-family: Poppins;
    font-style: normal;
    font-weight: bold;
    font-size: 40px;
    text-transform: uppercase;
    color: #FFFFFF;
    padding-top: 40px;
    @media only all and (max-width: 570px) {
        font-size: 24px;
        padding-top: 30px;
        padding-bottom: 50px;
    }
`;
const MultCar = styled.img`
    @media only all and (max-width: 570px) {
        width: auto;
        height: 100px;
    }
`;
const Logo = styled.img`
    position: absolute;
    right: -1.5%;
    top: -35%;
    @media only all and (max-width: 570px) {
        width: 80px;
        height: 91px;
        right: -6%;
        top: -15%;
    }
`;
const BlackSquare = styled.div`
    font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Arial, sans-serif;
    position: relative;
    height: 601px;
    background: #1D1D1B;
    border-radius: 20px;
    margin-top: 40px;
    margin-bottom: 24px;
    @media only all and (max-width: 570px) {
        height: auto;
        margin-top: 50px;
        padding-bottom: 80px;
    }
`;
const BlackText = styled.div`
    font-weight: bold;
    font-size: 32px;
    line-height: 42px;
    text-transform: uppercase;
    color: #FFFFFF;
    position: relative;
    z-index: 2;
    ${props => props.one && css`
        right: 145px;
        top: 80px;
    `}
    ${props => props.two && css`
        right: -20px;
        top: 160px;
    `}
    ${props => props.tree && css`
        right: 40px;
        top: 215px;
    `}
    ${props => props.four && css`
        top: 265px;
        left: 125px;
    `}
    @media only all and (max-width: 570px) {
        right: 0;
        top: 0;
        left: 0;
        font-size: 24px;
        margin-bottom: 30px;
    }
`;
const CreditCard = styled.img`
    position: absolute;
    left: -13%;
    top: -26%;
    @media only all and (max-width: 1680px) {
        left: -28%;
    }
    @media only all and (max-width: 570px) {
        width: 98px;
        height: 86px;
        left: -3%;
        top: -22%;
    }
`;
const Like = styled.img`
    position: absolute;
    right: -10%;
    top: -23%;
    @media only all and (max-width: 1680px) {
        right: -30%;
    }
    @media only all and (max-width: 570px) {
        width: auto;
        height: 100px;
        right: -5%;
        top: -22%;
    }
`;
const CarVendo = styled.img`
    position: absolute;
    left: -10%;
    bottom: -18%;
    @media only all and (max-width: 1680px) {
        left: -27%;
    }
    @media only all and (max-width: 570px) {
        width: auto;
        height: 142px;
        bottom: -5%;
        left: -1%;
    }

`;
const Circle = styled.div`
    position: absolute;
    width: 100px;
    height: 100px;
    background: #8D120C;
    border-radius: 100px;
    ${props => props.one && css`
        left: 120px;
        bottom: 230px;
        @media only all and (max-width: 1680px) {
            left: 10px;
        }
        @media only all and (max-width: 570px) {
            left: 0;
            bottom: 230px;
            width: 75px;
            height: 75px;
        }
    `}
    ${props => props.two && css`
        right: 85px;
        top: 170px;
        @media only all and (max-width: 1680px) {
            right: 10px;
        }
        @media only all and (max-width: 570px) {
            right: 0;
            top: 40px;
            width: 63px;
            height: 63px;
        }
    `}
    ${props => props.tree && css`
        right: 85px;
        bottom: 85px;
        @media only all and (max-width: 1680px) {
            right: 10px;
        }
        @media only all and (max-width: 570px) {
            right: 0;
            bottom: 35px;
            width: 83px;
            height: 83px;
        }
    `}
`;
const RedArrowContainer = styled.div`
    position: relative;
    height: 80px;
    @media only all and (max-width: 570px) {
        height: auto;
    }
`;
const RedArrow = styled.button`
    position: absolute;
    top: 0;
    right: 0;
    width: 540px;
    height: 78px;
    background: #8D120C;
    border-radius: 38.5px;
    padding-top: 10px;
    padding-bottom: 10px;
    padding-left: 35px;
    padding-right: 10px;
    @media only all and (max-width: 570px) {
        position: relative;
        width: auto;
        height: auto;
        padding-left: 15px;
    }
`;
const Contatto = styled.div`
    text-decoration-line: underline;
    text-transform: uppercase;
    font-weight: 700;
    font-size: 32px;
    padding-left: 0;
    padding-right: 0;
    padding-top: 5px;
    color: #FFFFFF;
    @media only all and (max-width: 570px) {
        font-size: 24px;
        padding-top: 10px;
    }
`;
const BlackArrow = styled.img`
    width: 57px;
    height: 57px;
    background: #1D1D1B;
    border-radius: 40px;
    margin: 0 10px 10px 15px;
    @media only all and (max-width: 570px) {
        margin: 0 0px 0px 15px;
    }
    @media only all and (max-width: 438px) {
        margin: 15px 0px 0px 15px;
    }
`;
const TitleGuarda = styled.div`
    font-weight: bold;
    font-size: 34px;
    line-height: 41px;
    text-transform: uppercase;
    color: #FFFFFF;
    margin-top: 40px;
    margin-bottom: 24px;
    @media only all and (max-width: 570px) {
        font-size: 24px;
    }
`;

const BlackRectangle = styled.div`
    position: absolute;
    top: 0;
    left: -400px;
    background: #1D1D1B;
    width: 1230px;
    height: 446px;
    border-radius: 20px;
    z-index: 0;

    @media only all and (max-width: 570px) {
        display: none;
    }
`;
const Video = styled.iframe`
    text-align: right;
    position: relative;
    z-index: 1;
    border: none;
    outline: none;
    margin-top: 24px;
    @media only all and (max-width: 570px) {
        height: 250px;
        border-radius: 10px;
        width: auto;
    }
`;
const InfoTitle = styled.div`
    font-weight: bold;
    font-size: 20px;
    line-height: 24px;
    text-transform: uppercase;
    color: #FFFFFF;
    background: #1D1D1B;
    border-radius: 20px;
    margin-left: 50px;
    padding-top: 25px;
    padding-bottom: 25px;
    @media only all and (max-width: 570px) {
        margin-left: 0px;
        padding-left: 5px;
        padding-right: 5px;
    }
    a :hover{
        color: #007bff;
        text-decoration-line: underline;
    }
`;
const InfoTextBox = styled.div`
    background: #1D1D1B;
    border-radius: 20px;
    font-weight: normal;
    font-size: 20px;
    color: #FFFFFF;
    text-align: left;
    padding-left: 10px;
    padding-bottom: 20px;
    margin-top: 15px;
    margin-left: 50px;
    @media only all and (max-width: 570px) {
        margin-left: 0px;
    }
`;
const SmallIcon = styled.img`
    width: 30px;
    height: 30px;
    margin-right: 10px;  
    ${props => props.one && css`
        margin-top: 15px;
        @media only all and (max-width: 570px) {
            margin-top: 12px;
        }
    `}
`;
const InfoText = styled.div`
    padding-top: 40px;
    color: #fff;
    ${props => props.t1 && css`
    :hover{
        color: #007bff;
        text-decoration-line: underline;
    }
    `}
    ${props => props.one && css`
        margin-left: 38px;
    `}
`;
const BigText = styled.div`
    font-style: italic;
    font-weight: normal;
    font-size: 30px;
    line-height: 66px;
    text-align: left;
    & > div{
        margin-top: 60px;
    }
     @media only all and (max-width: 570px) {
        font-size: 24px;
        line-height: 48px;
    }
`;
const BigCircle = styled.div`
    {/*Pureshka252203$*/}
    position: absolute;
    width: 570px;
    height: 570px;
    background: #8D120C;
    border-radius: 700px;
    ${props => props.one && css`
        right: -600px;
        top: 710px;
    `}
    ${props => props.two && css`
        left: -600px;
        top: 1300px;
    `}
    ${props => props.tree && css`
        right: -600px;
        top: 2000px;
    `}
    @media only all and (max-width: 570px) {
        display: none;
    }
`;
const Br1 = styled.br`
    display: none;
    @media only all and (max-width: 570px) {
        display: block;
    }
`;
const Br2 = styled.br`
    display: block;
    @media only all and (max-width: 570px) {
        display: none;
    }
`;
const GreyContainer = styled.div`
    background: #2E2E2E;
    border-radius: 20px;
    width: 100%;
    margin-top: 125px;
    margin-bottom: 60px;
`;
const FormTitle = styled.div`
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    padding-left: 70px;
    padding-right: 70px;
    text-align: center;
    @media only all and (max-width: 570px) {
        padding-left: 18%;
        padding-right: 18%;
    }
    @media only all and (max-width: 400px) {
        padding-left: 10%;
        padding-right: 10%;
    }
    & > img{
        flex: 0 0 50%;
        max-width: 50%;
        position: relative;
        width: 420px;
        height: 178px;
        margin-top: -70px;
        @media only all and (max-width: 570px) {
            width: 270px;
            height: 110px;
            flex: 0 0 auto;
            max-width: none;
        }
    }
    & > div{
        flex: 0 0 50%;
        max-width: 50%;
        position: relative;
        width: 100%;
        font-weight: 700;
        font-size: 40px;
        line-height: 100%;
        margin-top: 25px;
        @media only all and (max-width: 570px) {
            flex: 0 0 100%;
            max-width: 100%;
            font-size: 24px;
        }
    }
`;
const FormBody = styled.div`
    padding-left: 280px;
    padding-right: 280px;
    margin-top: 20px;
    margin-bottom: 40px;
    @media only all and (max-width: 570px) {
        padding-left: 15px;
        padding-right: 15px;
    }
    & > div{
        width: 100%;
        display: flex;
        flex-wrap: wrap;
        margin-top: 20px;
    }
    & > div > div > img{
        width: 30px;
        height: 30px;
    }
    & > div > div:nth-child(1){
        flex: 0 0 15%;
        max-width: 15%;
        position: relative;
        width: 100%;
        padding-top: 50px;
        @media only all and (max-width: 570px) {
            padding-top: 40px;
        }
    }
    & > div > div:nth-child(2){
        flex: 0 0 85%;
        max-width: 85%;
        position: relative;
        width: 100%;
        font-size: 24px;
        text-align: left;
        text-transform: capitalize;
        color: #C4C4C4;
        @media only all and (max-width: 570px) {
            font-size: 20px;
        }
    }
    & > div > div > div{
        padding-left: 30px;
        @media only all and (max-width: 570px) {
            padding-left: 15px;
        }
    }
    & input{
        width: 100%;
        background: #C4C4C4;
        box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
        border-radius: 20px;
        height: 60px;
        padding-left: 20px;
    }
`;
const FormFooter = styled.div`
    padding-left: 290px;
    padding-right: 290px;
    padding-bottom: 20px;
    @media only all and (max-width: 570px) {
        padding-left: 15px;
        padding-right: 15px;
    }
    & > div{
        width: 100%;
        display: flex;
        flex-wrap: wrap;
        margin-top: 20px;
    }
    & input{
        height: 30px;
        width: 30px;
    }
     & > div > div:nth-child(1){
        flex: 0 0 15%;
        max-width: 15%;
        position: relative;
        width: 100%;
        padding-top: 5px;
    }
    & > div > div:nth-child(2){
        flex: 0 0 85%;
        max-width: 85%;
        position: relative;
        width: 100%;
        font-size: 24px;
        text-align: left;
        color: #C4C4C4;
        @media only all and (max-width: 570px) {
            font-size: 20px;
        }
    }
`;
const FormWarning = styled.div`
    padding-left: 300px;
    padding-right: 300px;
    width: 100%;
    color: #C4C4C4;
    font-weight: 400;
    font-size: 24px;
    @media only all and (max-width: 570px) {
        padding-left: 15px;
        padding-right: 15px;
        font-size: 20px;
    }
    & a{
        color: #01579B;
    }
`; 
const FormButton = styled.div`
    padding-left: 300px;
    padding-right: 300px;
    @media only all and (max-width: 570px) {
        padding-left: 15px;
        padding-right: 15px;
    }
    padding-top: 10px;
    & input[type=button] {
        height: 60px;
        width: 100%;
        background: #01579B;
        border-radius: 20px;
        color: white;
        font-size: 24px;
        font-weight: 700;
        margin-top: 3px;
        margin-bottom: 30px;
        text-transform: uppercase;
    }
`; 
export default function Vendo_compro_auto() {
    const testRef = useRef(null);
    const scrollToElement = () => testRef.current.scrollIntoView();
    const [name_auto, setName] = useState("");
    const [tipo_motore, setTipo_motore] = useState("");
    const [anno, setAnno] = useState("");
    const [km, setKm] = useState("");
    const [email, setEmail] = useState("");
    const [phone, setPhone] = useState("");
    const [textbutton, setTextbutton] = useState("Invia informazioni");
    const [checkbox1, setCheckbox1] = useState(false);
    const [checkbox2, setCheckbox2] = useState(false);
    let formField = new FormData();
    async function AddClient() {
        const csrftoken = Cookies.get('csrftoken');
        formField.append("name", name_auto);
        formField.append("tipo_motore", tipo_motore);
        formField.append("anno", anno);
        formField.append("km", km);
        formField.append("email", email);
        formField.append("phone", phone);
        setName("");
        setTipo_motore("");
        setAnno("");
        setKm("");
        setEmail("");
        setPhone("");
        setCheckbox1(false);
        setCheckbox2(false);
        await fetch("api/form/", {
            method: 'POST',
            headers: { "X-CSRFToken": csrftoken },
            body: formField
        }).then((response) => {console.log(response.data);})
        setTextbutton("Grazie!");
    }

    return(
        <>
        <Title/>
        <Container className="col-sm-8 mx-auto text-center"> 
        <MultCar src="/static/images/mult_car.png"></MultCar>
        <RedRectangle> 
            <Logo src="/static/images/logo4.svg"></Logo>
            <RedTitle>
                Perchè vendere la tua auto a noi?
            </RedTitle>
            <RedText>
                Perchè conviene!
            </RedText>
        </RedRectangle>       
        </Container>
        <div className="col-sm-8 mx-auto text-center">
            <BlackSquare>
                <CreditCard src="/static/images/credit_card.png"></CreditCard>
                <Like src="/static/images/like.png"></Like>
                <BlackText one>Pagamento immediato</BlackText>
                <BlackText two>Passaggio di<Br1/> proprieta immediato</BlackText>
                <BlackText tree>Valore quotazione<Br1/> dichiarata da<br></br> Quatroruote</BlackText>
                <BlackText four>Garanzia di sicurezza e<Br2/> PROFESSIONALITÀ</BlackText>
                <CarVendo src="/static/images/car_vendo.png"></CarVendo>
                <Circle one></Circle>
                <Circle two></Circle>
                <Circle tree></Circle>
            </BlackSquare>
            <RedArrowContainer>
                <RedArrow className="row mx-auto" onClick={scrollToElement}>
                    <SmallIcon src="/static/images/whatsapp.svg" one></SmallIcon>
                    <Contatto className="col">CONTATTO IMMEDIATO</Contatto>
                    <BlackArrow src="/static/images/Arrow_bottom.svg"></BlackArrow>
                </RedArrow>
            </RedArrowContainer>
        </div>
        <Container className="col-sm-8 mx-auto text-center row"> 
        <TitleGuarda className="mx-auto">guarda il video in modo da non dover leggere</TitleGuarda>

        <div className="col-sm-8">
        <BlackRectangle>
        </BlackRectangle>
        <Video width="700" height="400" src="https://www.youtube-nocookie.com/embed/J-BHTW9USe0" title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowFullScreen></Video>
        </div>
        <div className="col-sm-4">
            <InfoTitle>VENDITA & ACQUISTO AUTO <a href="https://www.tanteauto.it" target="_blank" rel="noopener noreferrer">www.tanteauto.it</a></InfoTitle>
            <InfoTextBox>
            <InfoText one>Fabrizio Palpon</InfoText>
            <a href="https://api.whatsapp.com/send?phone=393939893909" target="_blank" rel="noopener noreferrer"><InfoText t1><SmallIcon src="/static/images/whatsapp.svg"></SmallIcon>+39 393 989 3909</InfoText></a>
            <InfoText><SmallIcon src="/static/images/mail-red.png"></SmallIcon>fabrizio@tanteauto.it</InfoText>
            <InfoText className="row">
                <SmallIcon src="/static/images/location.svg"></SmallIcon>
                <div className="col p-0">via Montorfano<br></br> 74/76 Seregno</div>
            </InfoText>
            </InfoTextBox>
        </div>
        <BigText>
            <div>Guarda che non leggerai le solite cose tipo noi facciamo il raviolo quadrato dal 1921 e mio nonno ha brevettato la mutanda invisibile e altre cavolate simili, se leggerai queste 4 righe conoscerai la vera storia di….</div>
            <div>Iniziamo Questa è la storia di uno di noi nato per caso in via Gluck, in una casa fuori città gente tranquilla che lavorava….. cantava Adriano Celentano.</div>
            <div>La mia canzone ha nel testo anche “mangiava con Quattroruote e conosceva tutte le auto….”, uno che voleva fare lo psicologo è invece è stato divorato dalla passione</div>
            <div>Così da una passione troppo forte per i motori lo studio era in secondo piano, quindi al lavoro di giorno e a scuola di sera, una volta si usava cosi…</div>
            <div>E mi divertivo così tanto che alla fine ho aperto un business proprio nella manutenzione delle auto, poi altri due e poi uno che si occupa solo di vendita auto.</div>
            <div>Quindi apro la mia prima società nel campo della manutenzione delle auto nel 1989, poi nel 1999 nuova sede, nel 2006 la seconda società, nel 2009 inizio a vendere auto nella prima società, nel 2014 apro un autosalone, sempre a Seregno, nel 2017 inizio la vendita On Line con anche consegna a casa del cliente, isole comprese, e poi avrei un altro progetto in cantiere ma vorrei farti una sorpresa più avanti :-D</div>
            <div>Ma a parte quello che ho fatto, il motivo per cui l’ho fatto e lo faccio è perché mi diverte tanto il mio lavoro. </div>
            <div>Mi piace il contatto con le persone oneste e corrette, gentili e intelligenti, se poi hanno anche passione per le auto è il massimo, ma devo dire che a volte gli incontri più belli sono quelli con le persone che vivono l’auto come mezzo di spostamento, e cosi si parla di vita e di esperienze reciproche.</div>
            <div>E allora se tu stai leggendo queste righe significa solo due cose, o sei solo curioso oppure hai il problema di dovere vendere la tua auto. </div>
            <div>E se hai un problema, ti do una bella notizia… io ho la soluzione! Ti compro la tua macchina e tac , il problema scompare grazie a <a href="https://quicomproauto.it/" target="_blank" rel="noopener noreferrer" >www.quicomproauto.it</a></div>
            <div>Quindi Se vuoi essere felice dopo averla venduta devi venderla a me! Questo è il mantra che ripeto a tutte le persone con cui entro in contatto, perché faccio il mio lavoro con grande amore e passione e onesta, e vedere felici i miei clienti è il motivo per cui mi piace fare il mio lavoro, e cosi la passione si alimenta ogni giorno 😊 
            <br ref={testRef}></br>Quindi non indugiare, affidati ad un professionista serio e onesto cosi sarai felice anche tu! Con affetto, stima simpatia e tanti auguri di una vita felice, Fabrizio Palpon.    
            </div>
            <BigCircle one></BigCircle>
            <BigCircle two></BigCircle>
            <BigCircle tree ></BigCircle>
        </BigText>
        <GreyContainer>
            <FormTitle>
                <img alt="" src="/static/images/car_form.png" ref={testRef}/>
                <div>VENDO COMPRO AUTO</div>
            </FormTitle>
            <FormBody>
                <div>
                  <div><img alt="" src="/static/images/Public_Transportation.png"/></div>
                  <div>
                    <div>Modello auto</div>
                    <input type="text" value={name_auto} onChange={e => setName(e.target.value)}/>
                  </div>
                </div>
                <div>
                  <div><img alt="" src="/static/images/Engine.png"/></div>
                  <div>
                    <div>Tipo motore</div>
                    <input type="text" value={tipo_motore} onChange={e => setTipo_motore(e.target.value)}/>
                  </div>
                </div>
                <div>
                  <div><img alt="" src="/static/images/Calendar.png"/></div>
                  <div>
                    <div>Anno di immatricolazione</div>
                    <input type="text" value={anno} onChange={e => setAnno(e.target.value)}/>
                  </div>
                </div>
                <div>
                  <div><img alt="" src="/static/images/Wheel.png"/></div>
                  <div>
                    <div>Km</div>
                    <input type="text" value={km} onChange={e => setKm(e.target.value)}/>
                  </div>
                </div>
                <div>
                  <div><img alt="" src="/static/images/iPhone.png"/></div>
                  <div>
                    <div>Telefono</div>
                    <input type="text" value={phone} onChange={e => setPhone(e.target.value)}/>
                  </div>
                </div>
                <div>
                  <div><img alt="" src="/static/images/Mail_Grey.png"/></div>
                  <div>
                    <div>E-Mail</div>
                    <input type="text" value={email} onChange={e => setEmail(e.target.value)}/>
                  </div>
                </div>
            </FormBody>
            <FormFooter>
                <div>
                    <div><input type="checkbox" name="Link" value="#" checked={checkbox1} onChange={e => setCheckbox1(e.target.checked)}></input></div>
                    <div>
                        <div>Autorizzazioni al contatto</div>
                    </div>
                </div>
                <div>
                    <div><input type="checkbox" name="Link" value="#" checked={checkbox2} onChange={e => setCheckbox2(e.target.checked)}></input></div>
                    <div>
                        <div>ACCETTO l'informativa e i consensi ai trattamenti sulla privacy.</div>
                    </div>
                </div>
            </FormFooter>
            <FormWarning>
                <div>Non tutti i consensi sono obbligatori, <Link to="/Privacy">Clicca qui per gestirli</Link> </div>
            </FormWarning>
            <FormButton><input type="button" value={textbutton} onClick={AddClient}></input></FormButton> 
        </GreyContainer>
        </Container>
        <VantaggiNoleggio/>
        </>
    );
}
