import React from "react";
import styled from 'styled-components';
const Section = styled.div`
    color: #fff;
    @media only all and (max-width: 570px) {
        margin-left: 20px;
        margin-right: 20px;
        margin-top: 95px;
    }
`;
const TitleStyle = styled.div`
    font-size: 60px;
    text-transform: uppercase;
    font-family: 'Poppins', sans-serif;
    color: #8D120C;
    height: 60px;
    font-weight: 900;
    margin-bottom: 10px;
    @media only all and (max-width: 570px) {
        font-size: 40px;
    }
`;
const Subtitle = styled.div`
    font-size: 20px;
    & > span{
        color: #8D120C; 
    }
    @media only all and (max-width: 570px) {
        font-size: 18px;
    }
`;
const Description = styled.div`
    font-size: 34px;
    font-style: Italic;
    letter-spacing: 5%;
    @media only all and (max-width: 570px) {
        font-size: 25px;
    }
`;
function Title() {
    return(
    <>
    <Section className="text-center">
        <TitleStyle>Palponauto</TitleStyle>
        <Subtitle>noleggio <span>lungo</span> e <span>breve</span> termine</Subtitle>
        <Description>Vogliamo portare nel mondo la libert√† di muoversi ovunque</Description>
    </Section>
    </>
    );
}
export {Title};
