import React, { useState, useEffect } from "react";
import styled, {css} from 'styled-components';
import { useParams } from "react-router-dom";
import Form from "./form";
import CartRow from './cart';
import Top from "./Top";
const Back = styled.div`
    width: 100%;
    height: 100%;
    background: #F1EBE3;
    margin-top: -50px;
`;
const Container = styled.div`
    font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Arial, sans-serif;
    color: #000000;
    text-align: center;
    padding-bottom: 60px;
    padding-top: 50px;
    @media only all and (max-width: 570px) {
        margin-top: 95px;
    }
    flex: 0 0 70%;
    max-width: 70%;
    position: relative;
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    margin-left: auto;
    margin-right: auto;    
    @media only all and (max-width: 1680px) {
        max-width: 80%!important;
        flex: 0 0 80%!important;
    }
    @media only all and (max-width: 1440px) {
    	max-width: 100%!important;
    	flex: 0 0 100%!important;
    }
`;
const ColonOne = styled.div`
    flex: 0 0 41.666667%;
    max-width: 41.666667%;
    position: relative;
    width: 100%;
    padding-right: 40px;
    margin-top: 150px;
    @media only all and (max-width: 570px) {
         max-width: 100%!important;
        flex: 0 0 100%!important;
        padding-right: 20px;
        padding-left: 20px;
        margin-top: 0px;
    }
`;
const ColonTwo = styled.div`
    flex: 0 0 58.333333%;
    max-width: 58.333333%;
    position: relative;
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    padding-left: 10px;
    padding-right: 10px;
    @media only all and (max-width: 570px) {
        max-width: 100%!important;
        flex: 0 0 100%!important;
        padding-left: 20px;
        padding-right: 20px;
        padding-top: 40px;
    }
`;
const Cambia = styled.div`
    font-weight: bold;
    font-size: 18px;
    letter-spacing: 0.05em;
    color: #01579B;
    text-align: right!important;
    background: #DAA520;
    border-radius: 45px;
    padding-top: 30px;
    padding-right: 40px;
    padding-bottom: 30px;
    padding-left: 40px;
    margin-bottom: 15px;
    margin-right: -20px;
    @media only all and (max-width: 570px) {
        margin-top: 20px;
    }
`;
const CarWrap = styled.div`
    height: 317px;
    overflow: hidden;
    width: 100%;
    position: relative;
`;
const Car = styled.img`
    width: 100%;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
`;
const Line = styled.div`
    background: #8D120C;
    height: 10px;
    margin-left: 80px;
    @media only all and (max-width: 570px) {
        margin-left: 0px;
    }
`;
const CarName = styled.div`
    font-size: 24px;
    margin-right: 10px;
    font-weight: 700;
    @media only all and (max-width: 570px) {
        font-size: 20px;
    }
`;
const BlockCambia = styled.div`
    display: flex;
    width: 100%;
    justify-content: end;
    @media only all and (max-width: 570px) {
        justify-content: center;
    }
`;
const Section = styled.div`
    color: #000000;
    text-align: center;
    margin-left: auto;
    margin-right: auto;
    @media only all and (max-width: 570px) {
        display: none;
        &{
            display: none;
        }
    }
`;
const TitleStyle = styled.div`
    font-size: 40px;
    text-transform: uppercase;
    font-family: 'Poppins', sans-serif;
    color: #8D120C;
    font-weight: 900;
    @media only all and (max-width: 570px) {
        font-size: 40px;
    }
`;
const Subtitle = styled.div`
    font-size: 14px;
    & > span{
        color: #8D120C; 
    }
    @media only all and (max-width: 570px) {
        font-size: 18px;
    }
`;
const Label = styled.div`
    font-size: 18px;
    text-transform: uppercase;
    font-weight: 700;
    color: #000000;
    background: #FFD700;
    border-radius: 10px;
    padding-left: 60px;
    padding-right: 60px;
    padding-bottom: 15px;
    padding-top: 15px;
    margin-top: 30px;
    margin-bottom: 20px;
    @media only all and (max-width: 570px) {
        background: #DAA520;
    }
`;
const FourSquares = styled.div`
    width: 100%;
    display: flex;
    flex-wrap: wrap;

`;
const Square = styled.div`
    flex: 0 0 50%;
    max-width: 50%;
    position: relative;
    padding: 15px;
    & > div{
        height: 230px;
        border-radius: 50px;
    }
    ${props => props.one && css`
        & > div{
            background: #FF7A00;
        }  
    `}
    ${props => props.two && css`
        & > div{
            background: #84D268;  
        } 
    `}
    ${props => props.tree && css`
        & > div{
            background: #8BC5F3; 
        }
    `}
    ${props => props.four && css`
        & > div{
            background: #DAA520;
        } 
    `}
    @media only all and (max-width: 570px) {
        flex: 0 0 100%;
        max-width: 100%;
    }
`;
const CambiaAnticipo = styled.div`
    width: 100%;
    background: #8D120C;
    border-radius: 50px;
    height: 140px;
    margin-top: 25px;
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    @media only all and (max-width: 570px) {
        margin-top: 25px;
        height: auto;
        display: block;
    }
`;
const AnticipoText = styled.div`
    font-weight: 700;
    font-size: 30px;
    color: #FFFFFF;
    -ms-flex-preferred-size: 0;
    flex-basis: 0;
    -ms-flex-positive: 1;
    flex-grow: 1;
    max-width: 100%;
    margin-top: 45px;

`;
const CambiaButton = styled.button`
    font-weight: 700;
    font-size: 44px;
    color: #FFFFFF;
    width: 350px;
    height: 100px;
    background: #1D1D1B;
    border-radius: 50px;
    -ms-flex-preferred-size: 0;
    flex-basis: 0;
    ms-flex-positive: 1;
    flex-grow: 1;
    max-width: 100%;
    margin-top: 20px;
    margin-right: 10px;
    & > img{
        width: 60px;
        height: 60px;
    }
    @media only all and (max-width: 570px) {
        margin-right: 0px;
        margin-bottom: 30px;
    }
`;
const Characteristics = styled.div`
    font-size: 21px;
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    margin-top: 5px;
    & > div{
        -ms-flex-preferred-size: 0;
        flex-basis: 0;
        -ms-flex-positive: 1;
        flex-grow: 1;
        max-width: 100%;
        width: 100%;
        display: flex;
        flex-wrap: wrap;

    }
    & > div > img {
        -ms-flex-preferred-size: 0;
        flex-basis: 0;
        -ms-flex-positive: 1;
        flex-grow: 1;
        max-width: 100%;
        width: 60px;
        height: 60px;
    }
    & > div > div {
        -ms-flex-preferred-size: 0;
        flex-basis: 0;
        -ms-flex-positive: 1;
        flex-grow: 1;
        max-width: 100%;
        margin-top: 15px;
        margin-bottom: 10px;
    }
    @media only all and (max-width: 570px) {
        & > div{
            display: block;
        }
    }

`;
const Check = styled.div`
    font-size: 24px;
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    margin-left: 50px;
    margin-top: 10px;
    & > div{
        width: 100%;
        display: flex;
        flex-wrap: wrap;
        margin-top: 15px;
    }
    & > div > div {
        margin-left 15px;
    }
    @media only all and (max-width: 570px) {
        font-size: 21px;
        margin-left: 20px;
    }
`;
const ShadowBlock = styled.div`   
    background: #EDE7DF;
    border-radius: 10px;
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    padding-bottom: 40px;
    padding-top: 20px;

`;
const ShadowBlock2 = styled.div`
    background: #EDE7DF;
    border-radius: 10px;
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    padding-bottom: 30px;
    padding-left: 15px;
    padding-right: 15px;
    @media only all and (max-width: 570px) {
        border-radius: 0px;
        box-shadow: none;
        background: rgb(241, 235, 227) none repeat scroll 0% 0%;
        padding-left: 0px;
        padding-right: 0px;
    }
`;
const BlockInfo = styled.div`
    font-size: 24px;
    line-height: 150%;
    text-align: center;
    color: #4F4F4F;
    margin-top: 30px;
    & > div{
        margin-left: 170px;
        margin-right: 170px;
    }
     @media only all and (max-width: 570px) {
        & > div{
            margin-left: 20px;
            margin-right: 20px;
        }
    }
`;
const Info = styled.img`
    width: 60px;
    height: 60px;
`;
const Mar = styled.div`
    padding-bottom: 50px;
    @media only all and (max-width: 570px) {
        margin-left: 20px!important;
        margin-right: 20px!important;
    }
`;
const AnticipoBlock = styled.div`
    position: absolute;
    right: 87px;
    bottom: 150px;
    /*bottom: -400px;*/
    & > div{
        font-weight: 700;
        font-size: 44px;
        color: #FFFFFF;
        width: 250px;
        height: 90px;
        padding-top: 10px;
        background: #1D1D1B;
        cursor: pointer;
    }
    & > .selected{
        background: #C4C4C4;
    }
    @media only all and (max-width: 570px) {
        right: 25%;
        bottom: 160px;
    }
    @media only all and (max-width: 500px) {
        right: 23%;
    }
    @media only all and (max-width: 400px) {
        right: 15%;
    }
`;
const Price = styled.div`
    font-weight: 800;
    font-size: 25px;
    padding-top: 10px;
    color: #000000;
    text-transform: uppercase;
    & > span{
        font-size: 50px;
    }

`;
const SquareText = styled.div`
    font-weight: 400;
    font-size: 24px;
    margin-top: 5px;
`;
/* Mobile version */
const SectionMobile = styled.div` 
    display: none;
    &{
        display: none;
    }
    @media only all and (max-width: 570px) {
        display: block;
        color: #000000;
        text-align: center;
        margin-left: 20px;
        margin-right: 20px;
        margin-top: 95px;
    }
`;
const Arrow = styled.img`
    width: 60px;
    height: 60px;
`;
export default function Details_Long() {
    const { id } = useParams();
    const [dates, setData] = useState([]);
    const [dataset, setDataset] = useState([]);
    const [months, setMounth] = useState([]);
    const [isOpen, setIsOpen] = useState(false);
    const toggling = () => setIsOpen(!isOpen);
    async function getData() {
        try {
            setMounth( await (await fetch(`/api/car/lungo/details/mounth/${id}/`)).json());
            const res = await (await fetch(`/api/car/lungo/details/${id}/`)).json();
            res.map((post) => setDataset(post));
            setData( await (await fetch(`/api/${res[0]?.tipo_di_macchina}/lungo`)).json());

        } catch (e) {
            console.log(e);
        }
    }
    useEffect(() => {
        getData();
    }, [id]);
    const [currentAnticipo, setAnticipo] = useState(0);
    return(
        <>
        <Back>
            <Container>
                <ColonOne>
                    <SectionMobile>
                        <TitleStyle>Palponauto</TitleStyle>
                        <Subtitle>noleggio <span>lungo</span> e <span>breve</span> termine</Subtitle>
                        <Label>noleggio LUNGO TERMINE</Label>
                    </SectionMobile> 
                    <BlockCambia>
                        <Cambia>CAMBIA L’OFFERTA</Cambia>
                    </BlockCambia>
                    <CarWrap>
                        <Car src={dataset.foto}></Car>
                    </CarWrap>
                    <Line></Line>
                    <ShadowBlock>
                        <BlockCambia>
                            <CarName>{ dataset.nome }</CarName>
                        </BlockCambia>
                        <Characteristics>
                            <div>
                                <img src="/static/images/Automatic.svg"/>
                                <div>{dataset.tipo_di_motore}</div>
                            </div>
                            <div>
                                <img src="/static/images/Engine.svg"/>
                                <div>{dataset.tipo_di_carburante}</div>
                            </div>
                            <div>
                                <img src="/static/images/Gas_Station.svg"/>
                                <div>{dataset.consumo}km</div>
                            </div>      
                        </Characteristics>
                        <Check>
                            <div>
                                <img src="/static/images/Check_Circle.svg"/>
                                <div>Manutenzione inclusa</div>
                            </div>
                            <div>
                                <img src="/static/images/Check_Circle.svg"/>
                                <div>Kasko completa</div>
                            </div>
                            <div>
                                <img src="/static/images/Check_Circle.svg"/>
                                <div>Ritiro usato in permuta</div>
                            </div>
                        </Check>
                    </ShadowBlock>
                </ColonOne>
                <ColonTwo>
                    <Section>
                        <TitleStyle>Palponauto</TitleStyle>
                        <Subtitle>noleggio <span>lungo</span> e <span>breve</span> termine</Subtitle>
                        <Label>noleggio LUNGO TERMINE</Label>
                    </Section> 
                    <ShadowBlock2>
                        <FourSquares>
                            <Square one>
                                <div>
                                    <Price><span>{months[currentAnticipo]?.mesi36}</span> euro i.e</Price>
                                    <SquareText>36 Mesi</SquareText>
                                    <SquareText>{months[currentAnticipo]?.anticipo} Anticipo</SquareText>
                                    <SquareText>km 10000</SquareText>
                                </div>
                            </Square>
                            <Square two>
                                <div>
                                    <Price><span>{months[currentAnticipo]?.mesi48}</span> euro i.e</Price>
                                    <SquareText>48 Mesi</SquareText>
                                    <SquareText>{months[currentAnticipo]?.anticipo} Anticipo</SquareText>
                                    <SquareText>km 10000</SquareText>
                                </div>
                            </Square>
                            <Square tree>
                                <div>
                                    <Price><span>{months[currentAnticipo]?.mesi60}</span> euro i.e</Price>
                                    <SquareText>60 Mesi</SquareText>
                                    <SquareText>{months[currentAnticipo]?.anticipo} Anticipo</SquareText>
                                    <SquareText>km 10000</SquareText>
                                </div>
                            </Square>
                            <Square four>
                                <div>
                                    <Price><span>{months[currentAnticipo]?.mesi72}</span> euro i.e</Price>
                                    <SquareText>72 Mesi</SquareText>
                                    <SquareText>{months[currentAnticipo]?.anticipo} Anticipo</SquareText>
                                    <SquareText>km 10000</SquareText>
                                </div>
                            </Square>
                        </FourSquares>
                        <CambiaAnticipo>
                            <AnticipoText>Cambia anticipo</AnticipoText>
                            <CambiaButton onClick={toggling}>
                                {months[currentAnticipo]?.anticipo}&#8364;&nbsp; 
                                {!isOpen && (<img src="/static/images/Collapse_Arrow_bottom.png"/>)}
                                {isOpen && ( <Arrow src="/static/images/Collapse_Arrow_top.svg"/>)}
                            </CambiaButton>
                                {isOpen && (
                                    <AnticipoBlock>
                                        <div onClick={() => { toggling(); setAnticipo(0);}} className={currentAnticipo==0 ? "selected" : ""}>{months[0]?.anticipo}&#8364;</div>
                                        <div onClick={() => { toggling(); setAnticipo(1);}} className={currentAnticipo==1 ? "selected" : ""}>{months[1]?.anticipo}&#8364;</div>
                                        <div onClick={() => { toggling(); setAnticipo(2);}} className={currentAnticipo==2 ? "selected" : ""}>{months[2]?.anticipo}&#8364;</div>
                                        <div onClick={() => { toggling(); setAnticipo(3);}} className={currentAnticipo==3 ? "selected" : ""}>{months[3]?.anticipo}&#8364;</div>
                                        <div onClick={() => { toggling(); setAnticipo(4);}} className={currentAnticipo==4 ? "selected" : ""}>{months[4]?.anticipo}&#8364;</div>
                                    </AnticipoBlock>
                                ) }
                        </CambiaAnticipo>
                    </ShadowBlock2>
                </ColonTwo>
                <BlockInfo>
