import React from "react";
import {Title} from './Title';
import styled, { css } from 'styled-components';
import {VantaggiNoleggio} from './vantaggi_noleggio';
import { Link } from "react-router-dom";

const Container = styled.div`
    font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Arial, sans-serif;
    @media only all and (max-width: 1440px) {
        max-width: 100%!important;
        flex: 0 0 100%!important;
    }
`;
const RedRectangle = styled.div`
    height: 264px;
    background: #8D120C;
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    border-radius: 20px;
    position: relative;
`;
const RedTitle = styled.div`
    font-family: Poppins;
    font-style: normal;
    font-weight: bold;
    font-size: 48px;
    text-transform: uppercase;
    color: #FFFFFF;
    padding-top: 60px;
`;
const RedText = styled.div`
    font-family: Poppins;
    font-style: normal;
    font-weight: bold;
    font-size: 40px;
    text-transform: uppercase;
    color: #FFFFFF;
    padding-top: 40px;
`;
const Logo = styled.img`
    position: absolute;
    right: -1.5%;
    top: -35%;
`;
const BlackSquare = styled.div`
    font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Arial, sans-serif;
    position: relative;
    height: 601px;
    background: #1D1D1B;
    border-radius: 20px;
    margin-top: 40px;
    margin-bottom: 24px;
`;
const BlackText = styled.div`
    font-weight: bold;
    font-size: 32px;
    line-height: 42px;
    text-transform: uppercase;
    color: #FFFFFF;
    position: relative;
    ${props => props.one && css`
        right: 145px;
        top: 80px;
    `}
    ${props => props.two && css`
        right: -20px;
        top: 160px;
    `}
    ${props => props.tree && css`
        right: 40px;
        top: 215px;
    `}
    ${props => props.four && css`
        top: 265px;
        left: 125px;
    `}
`;
const CreditCard = styled.img`
    position: absolute;
    left: -13%;
    top: -26%;
`;
const Like = styled.img`
    position: absolute;
    right: -10%;
    top: -23%;
`;
const CarVendo = styled.img`
    position: absolute;
    left: -10%;
    bottom: -18%;
`;
const Circle = styled.div`
    position: absolute;
    width: 100px;
    height: 100px;
