     return(
         <>
             <div className="HomeMain">
                 DFK Stake
                 <img src='./static/images/logo.png' />
             </div>
             <div className="AboutUsMain">
                 <div>
                     <div className="Text">This is a project in the innovative ecosystem - Web3.0.</div>
                     <div className="Image"><img src="../static/images/AboutUs/AboutUs1.png" /></div>
                 </div>
