import React, { useState, useEffect } from "react";
import styled, {css} from 'styled-components';
import { Link, useParams } from "react-router-dom";
import Form from "./form";
import CartRowBreve from "./cart_breve";
const Back = styled.div`
    width: 100%;
    height: 100%;
    background: #F1EBE3;
    margin-top: -50px;
`;
const Container = styled.div`
    font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Arial, sans-serif;
    color: #000000;
    text-align: center;
    padding-bottom: 60px;
    padding-top: 50px;
    @media only all and (max-width: 570px) {
        margin-top: 95px;
    }
    flex: 0 0 70%;
    max-width: 70%;
    position: relative;
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    margin-left: auto;
    margin-right: auto;    
    @media only all and (max-width: 1680px) {
        max-width: 80%!important;
        flex: 0 0 80%!important;
    }
    @media only all and (max-width: 1440px) {
    	max-width: 100%!important;
    	flex: 0 0 100%!important;
    }
`;
const ColonOne = styled.div`
    flex: 0 0 41.666667%;
    max-width: 41.666667%;
    position: relative;
    width: 100%;
    padding-right: 40px;
    margin-top: 253px;
    @media only all and (max-width: 570px) {
        max-width: 100%!important;
        flex: 0 0 100%!important;
        padding-right: 20px;
        padding-left: 20px;
        margin-top: 0px;
   }
`;
const ColonTwo = styled.div`
    flex: 0 0 58.333333%;
    max-width: 58.333333%;
    position: relative;
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    padding-left: 10px;
    padding-right: 10px;
    @media only all and (max-width: 570px) {
        max-width: 100%!important;
        flex: 0 0 100%!important;
        padding-left: 20px;
        padding-right: 20px;
        padding-top: 40px;
    }
`;
const BlockCambia = styled.div`
    display: flex;
    width: 100%;
    justify-content: end;
    @media only all and (max-width: 570px) {
        justify-content: center;
    }
`;
const CarWrap = styled.div`
    height: 317px;
    overflow: hidden;
    width: 100%;
    position: relative;
`;
const Car = styled.img`
    width: 100%;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
`;
const Line = styled.div`
    background: #8D120C;
    height: 10px;
    margin-left: 80px;
    @media only all and (max-width: 570px) {
        margin-left: 0px;
    }
`;
const CarName = styled.div`
    font-size: 24px;
    margin-right: 10px;
    font-weight: 700;
    @media only all and (max-width: 570px) {
        font-size: 20px;
    }
`;
const Section = styled.div`
    color: #000000;
    text-align: center;
    margin-left: auto;
    margin-right: auto;
    @media only all and (max-width: 570px) {
        display: none;
        &{
            display: none;
        }
    }
`;
const TitleStyle = styled.div`
    font-size: 40px;
    text-transform: uppercase;
    font-family: 'Poppins', sans-serif;
    color: #8D120C;
    font-weight: 900;
    @media only all and (max-width: 570px) {
        font-size: 40px;
    }
`;
const Subtitle = styled.div`
    font-size: 14px;
    & > span{
        color: #8D120C; 
    }
    @media only all and (max-width: 570px) {
        font-size: 18px;
    }
`;
const Label = styled.div`
    font-size: 18px;
    text-transform: uppercase;
    font-weight: 700;
    color: #FFFFFF;
    background: #232323;
    border-radius: 10px;
    padding-left: 60px;
    padding-right: 60px;
    padding-bottom: 15px;
    padding-top: 15px;
    margin-top: 30px;
    margin-bottom: 20px;
`;
const FourSquares = styled.div`
    width: 100%;
    display: flex;
    flex-wrap: wrap;

`;
const Square = styled.div`
    flex: 0 0 50%;
    max-width: 50%;
    position: relative;
    padding: 15px;
    & > div{
        height: 230px;
        border-radius: 50px;
    }
    ${props => props.one && css`
        & > div{
            background: #FF7A00;
        }  
    `}
    ${props => props.two && css`
        & > div{
            background: #84D268;  
        } 
    `}
    ${props => props.tree && css`
        & > div{
            background: #8BC5F3; 
        }
    `}
    ${props => props.four && css`
        & > div{
            background: #DAA520;
        } 
    `}
    @media only all and (max-width: 570px) {
        flex: 0 0 100%;
        max-width: 100%;
    }
`;
const CambiaAnticipo = styled(Link)`
    width: 100%;
    background: #8D120C;
    border-radius: 50px;
    height: 140px;
    margin-top: 25px;
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    color: #FFFFFF;
    :hover{
        text-decoration: none;
        color: #FFFFFF;
    }
    @media only all and (max-width: 570px) {
        margin-top: 25px;
        height: auto;
        display: block;
    }
`;
const Arrow = styled.img`
    width: 100px;
    height: 100px;
    background: rgb(29, 29, 27) none repeat scroll 0% 0%;
    border-radius: 50px;
    margin: 20px 20px 0px 0px;
    @media only all and (max-width: 570px) {
        margin-right: 0px;
        margin-bottom: 30px;
    }
`;
const WhatsApp = styled.img`
    width: 100px;
    height: 107px;
    margin: 17px 0px 0px 25px;
    @media only all and (max-width: 570px) {
        display: none;
    }     
`;
const AnticipoText = styled.div`
    font-weight: 700;
    font-size: 30px;
    color: #FFFFFF;
    -ms-flex-preferred-size: 0;
    flex-basis: 0;
    -ms-flex-positive: 1;
    flex-grow: 1;
    max-width: 100%;
    margin-top: 45px;

`;
const Characteristics = styled.div`
    font-size: 21px;
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    margin-top: 5px;
    & > div{
        -ms-flex-preferred-size: 0;
        flex-basis: 0;
        -ms-flex-positive: 1;
        flex-grow: 1;
        max-width: 100%;
        width: 100%;
        display: flex;
        flex-wrap: wrap;

    }
    & > div > img {
        -ms-flex-preferred-size: 0;
        flex-basis: 0;
        -ms-flex-positive: 1;
        flex-grow: 1;
        max-width: 100%;
        width: 60px;
        height: 60px;
    }
    & > div > div {
        -ms-flex-preferred-size: 0;
        flex-basis: 0;
        -ms-flex-positive: 1;
        flex-grow: 1;
        max-width: 100%;
        margin-top: 15px;
        margin-bottom: 10px;
    }
    @media only all and (max-width: 570px) {
        & > div{
            display: block;
        }
    }
`;
const Check = styled.div`
    font-size: 24px;
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    margin-left: 50px;
    margin-top: 30px;
    & > div{
        width: 100%;
        display: flex;
        flex-wrap: wrap;
        margin-top: 15px;
    }
    & > div > div {
        margin-left 15px;
    }
    @media only all and (max-width: 570px) {
        font-size: 21px;
        margin-left: 20px;
    }
`;
const ShadowBlock = styled.div`   
    background: #EDE7DF;
    border-radius: 10px;
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    padding-bottom: 70px;
    padding-top: 20px;
`;
const ShadowBlock2 = styled.div`
    background: #EDE7DF;
    border-radius: 10px;
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    padding-bottom: 30px;
    padding-left: 15px;
    padding-right: 15px;
    @media only all and (max-width: 570px) {
        border-radius: 0px;
        box-shadow: none;
        background: rgb(241, 235, 227) none repeat scroll 0% 0%;
        padding-left: 0px;
        padding-right: 0px;
    }
`;
const BlockInfo = styled.div`
    font-size: 24px;
    line-height: 150%;
    text-align: center;
    color: #4F4F4F;
    margin-top: 30px;
    & > div{
        margin-left: 170px;
        margin-right: 170px;
    }
    @media only all and (max-width: 570px) {
        & > div{
            margin-left: 20px;
            margin-right: 20px;
        }
    }
`;
const Info = styled.img`
    width: 60px;
    height: 60px;
`;
const Mar = styled.div`
    padding-bottom: 50px;
    @media only all and (max-width: 570px) {
        margin-left: 20px!important;
        margin-right: 20px!important;
    }
`;
const Price = styled.div`
    font-weight: 800;
    font-size: 25px;
    padding-top: 10px;
    color: #000000;
    text-transform: uppercase;
    & > span{
        font-size: 50px;
    }
`;
const SquareText = styled.div`
    font-weight: 400;
    font-size: 24px;
    margin-top: 5px;
`;
/* Mobile version */
const SectionMobile = styled.div` 
    display: none;
    &{
        display: none;
    }
    @media only all and (max-width: 570px) {
        display: block;
        color: #000000;
        text-align: center;
        margin-left: 20px;
        margin-right: 20px;
        margin-top: 95px;
    }
`;
export default function Details_Short() {
    const { id } = useParams();
    const [dates, setData] = useState([]);
    const [dataset, setDataset] = useState([]);
    async function getData() {
        try {
            //(await (await fetch(`/api/car/breve/details/${id}/`)).json()).map((post) => setDataset(post));
            const res = await (await fetch(`/api/car/breve/details/${id}/`)).json();
            res.map((post) => setDataset(post));
            setData( await (await fetch(`/api/${res[0]?.tipo_di_macchina}/breve`)).json());
        } catch (e) {
            console.log(e);
        }
    }
    useEffect(() => {
        getData();
    }, [id]);
    return(
        <>
        <Back>
            <Container>
                <ColonOne>
                    <SectionMobile>
                        <TitleStyle>Palponauto</TitleStyle>
                        <Subtitle>noleggio <span>lungo</span> e <span>breve</span> termine</Subtitle>
                        <Label>noleggio BREVE TERMINE</Label>
                    </SectionMobile> 
                    <CarWrap>
                        <Car src={dataset.foto}></Car>
                    </CarWrap>
                    <Line></Line>
                    <ShadowBlock>
                        <BlockCambia>
                            <CarName>{ dataset.nome }</CarName>
                        </BlockCambia>
                        <Characteristics>
                            <div>
                                <img alt="" src="/static/images/Automatic.svg"/>
                                <div>{dataset.tipo_di_motore}</div>
                            </div>
                            <div>
                                <img alt="" src="/static/images/Engine.svg"/>
                                <div>{dataset.tipo_di_carburante}</div>
                            </div>
                            <div>
                                <img alt="" src="/static/images/Gas_Station.svg"/>
                                <div>{dataset.consumo}km</div>
                            </div>      
                        </Characteristics>
                        <Check>
                            <div>
                                <img alt="" src="/static/images/Check_Circle.svg"/>
                                <div>Iva esclusa</div>
                            </div>
                            <div>
                                <img alt="" src="/static/images/Check_Circle.svg"/>
                                <div>Sconto se lungo periodo</div>
                            </div>
                        </Check>
                    </ShadowBlock>
                </ColonOne>
                <ColonTwo>
                    <Section>
                        <TitleStyle>Palponauto</TitleStyle>
                        <Subtitle>noleggio <span>lungo</span> e <span>breve</span> termine</Subtitle>
                        <Label>noleggio BREVE TERMINE</Label>
                    </Section> 
                    <ShadowBlock2>
                        <FourSquares>
                            <Square one>
                                <div>
                                    <Price><span>{dataset.per_giorno}</span> euro i.e</Price>
                                    <SquareText>Per giorno</SquareText>
                                </div>
                            </Square>
                            <Square two>
                                <div>
                                    <Price><span>{dataset.per_settimana}</span> euro i.e</Price>
                                    <SquareText>Per settimana</SquareText>
                                </div>
                            </Square>
                            <Square tree>
                                <div>
                                    <Price><span>{dataset.per_15giorni}</span> euro i.e</Price>
                                    <SquareText>Per 15 giorni</SquareText>
                                </div>
                            </Square>
                            <Square four>
                                <div>
                                    <Price><span>{dataset.per_mese}</span> euro i.e</Price>
                                    <SquareText>Per mese</SquareText>
                                </div>
                            </Square>
                        </FourSquares>
                        <CambiaAnticipo to="/Contatti">
                            <WhatsApp src="/static/images/whatsapp.svg"/>
                            <AnticipoText>CONTATTO IMMEDIATO</AnticipoText>
                            <Arrow src="/static/images/Collapse_Arrow_bottom.png"/>
                        </CambiaAnticipo>
                    </ShadowBlock2>
                </ColonTwo>
                <BlockInfo>
                    <Info src="/static/images/Big_Info.svg" />
                    <div>
                        Condizioni dell’offerta: L’offerta è soggetta a controllo disponibilità del prodotto scelto, ed è limitata all’approvazione dell’affidamento del Cliente da parte della società di noleggio. Le informazioni contenute in questa pagina sono puramente indicative e non possono costituire in nessun caso un impegno contrattuale. La foto qui pubblicata ha lo scopo di illustrare il prodotto, le immagini visualizzate sono puramente indicative, versioni, allestimenti potrebbero essere differenti
                    </div>
                </BlockInfo>
            </Container>
            <Mar className="row d-flex justify-content-center">
                <Form/>
            </Mar>
            <CartRowBreve date={dates}/>
        </Back>
        </>
    );
}
