import React from "react";
import { Link } from "react-router-dom";
import styled, { css } from 'styled-components';
import Title from './title';

const Container = styled.div`
    font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Arial, sans-serif;
    color: #FFFFFF;
    @media only all and (max-width: 570px) {
        margin-top: 95px;
    }
    flex: 0 0 66.666667%;
    max-width: 66.666667%;
    position: relative;
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    margin-left: auto;
    margin-right: auto;    
    @media only all and (max-width: 1680px) {
        max-width: 80%!important;
        flex: 0 0 80%!important;
    }
    @media only all and (max-width: 1440px) {
        max-width: 100%!important;
        flex: 0 0 100%!important;
}
`;
const BigButton = styled.button`
    background: #D3D3D3;
    color: #000000;
    font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Arial, sans-serif;
    font-style: normal;
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    border-radius: 10px;
    border: none;
    outline: none;
    height: 161px;
    margin-bottom: 56px;
    width: 100%;
    ${props => props.one && css`
        margin-right: 30px;
    `}
    ${props => props.dis && css`
        background: rgba(211, 211, 211, 0.1);
        box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    `}
    @media only all and (max-width: 570px) {
        margin-bottom: 30px;
        margin-right: 0px;
    }
`;
const TitleButton = styled.div`
    text-align: center;
    font-weight: bold;
    font-size: 32px;
    line-height: 38px;
    letter-spacing: 0.05em;
    text-transform: uppercase;
    color: #000000;

    @media only all and (max-width: 570px) {
        font-size: 24px;
    }
    ${props => props.pad && css`
        padding-top: 38px;
        @media only all and (max-width: 570px) {
            @media only all and (min-width: 355px) {
                margin-bottom: 25px;
                margin-top: 15px;
            }
        }
    `}
    ${props => props.two && css`
        @media only all and (max-width: 570px) {
            @media only all and (min-width: 344px) {
                margin-bottom: 10px;
                margin-top: 30px;
            }
        }
    `}
`;
const Scopri = styled.div`
    text-align: right;
    text-decoration-line: underline;
    text-transform: uppercase;
    font-weight: bold;
    font-size: 24px;
    position: relative;
    bottom: -20px;
    @media only all and (max-width: 570px) {
        bottom: 0px;
    }
`;
const Arrow = styled.img`
    text-align: right;
    margin-left: 5px;
    @media only all and (max-width: 570px) {
        width: 21px;
        height: 30px;
        margin-bottom: 3px;
    }
`;
const ContainerPadding = styled.div`
    @media only all and (max-width: 570px) {
        padding-left: 0;
        padding-right: 0;
    }
`;
const BigText = styled.div`
    margin-right: 15px;
    margin-left: 15px;
    margin-bottom: 70px;
`;
const Par = styled.div`
    margin-top: 60px;
    font-size: 32px;
    font-style: italic;
    line-height: 200%;
`;
const YellowText = styled.span`
    color: #FFD700;
`;
const Ball = styled.div`
    width: 570px;
    height: 570px;
    border-radius: 570px;
    position: absolute;
    background: #8D120C;
    ${props => props.one && css`
        right: -600px;
        top: 650px;
    `}
    ${props => props.two && css`
        left: -600px;
        top: 1870px;
    `}
    ${props => props.tree && css`
        right: -600px;
        top: 2900px;
    `}
    ${props => props.four && css`
        left: -600px;
        top: 4320px;
    `}
    ${props => props.five && css`
        right: -600px;
        top: 5350px;
    `}
    ${props => props.six && css`
        left: -600px;
        top: 6770px;
    `}
    ${props => props.seven && css`
        right: -600px;
        top: 7800px;
    `}
    ${props => props.eight && css`
        left: -600px;
        top: 9220px;
    `}
    ${props => props.nine && css`
        right: -600px;
        top: 10250px;
    `}
`;

export default function Vantaggi_Short() {
    return(
        <>
        <Title/>
        <Container>
            <Ball one/>
            <Ball two/>
            <Ball tree/>
            <Ball four/>
            <Ball five/>
            <Ball six/>
            <Ball seven/>
            <Ball eight/>
            <Ball nine/>
            <ContainerPadding className="col-sm-6">
                <Link to="/Privati_Short">
                    <BigButton one dis>
                        <TitleButton pad>Vantaggi per i privati</TitleButton>
                        <Scopri>Scopri di più<Arrow src="/static/images/arrow3.svg"></Arrow></Scopri>
                    </BigButton>
                </Link>
            </ContainerPadding>
            <ContainerPadding className="col-sm-6">
                <Link to="/Vantaggi_Short">
                    <BigButton>
                        <TitleButton two>Vantaggi per aziende e partita IVA</TitleButton>
                        <Scopri>Scopri di più<Arrow src="/static/images/arrow3.svg"></Arrow></Scopri>
                    </BigButton>
                </Link>
            </ContainerPadding>
            <BigText>

            </BigText>
        </Container>
        </>
    );
}
