import React from "react";
import {Title} from './title';
import styled, { css } from 'styled-components';
import {CartRow} from './cart';
import { Link } from "react-router-dom";
const testing_date = []
const RedRectangle = styled.div`
    background: #81100B;
    border-radius: 10px;
    text-align: center;
    padding: 42px 32px;
    margin-top: 72px;
    margin-bottom: 32px;
`;
const Text = styled.div`
    font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Arial, sans-serif;
    font-style: normal;
    font-weight: normal;
    font-size: 28px;
    letter-spacing: 0.05em;
    color: #FFFFFF;
    padding-bottom: 40px;
    ${props => props.none && css`
        padding-bottom: 0px;
    `}
    @media only all and (max-width: 500px) {
        font-size: 24px;
    }
`;
const Logo = styled.img`
    position: absolute;
    right: -4.3%;
    top: -7%;
    z-index: 1;
    text-align: right;
    @media only all and (max-width: 1440px) {
        right: 0%;
        top: -7%;
    }
    @media only all and (max-width: 500px) {
        width: 88px;
        height: 100px;
        right: -1%;
        top: 0%;
    }
`;
const BigButton = styled.button`
    background: #D3D3D3;
    font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Arial, sans-serif;
    font-style: normal;
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    border-radius: 10px;
    border: none;
    outline: none;
    height: 161px;
    margin-bottom: 56px;
    width: 100%;
    ${props => props.one && css`
        margin-right: 30px;
    `}
    @media only all and (max-width: 500px) {
        margin-bottom: 30px;
        margin-right: 0px;
        height: auto;
    }
`;
const TitleButton = styled.div`
    text-align: center;
    font-weight: bold;
    font-size: 32px;
    line-height: 38px;
    letter-spacing: 0.05em;
    text-transform: uppercase;
    color: #000000;
    @media only all and (max-width: 500px) {
        font-size: 24px;
        margin-bottom: 10px;
        margin-top: 30px;
    }
`;
const Scopri = styled.div`
    text-align: right;
    text-decoration-line: underline;
    text-transform: uppercase;
    font-weight: bold;
    font-size: 24px;
    position: relative;
    bottom: -20px;
    @media only all and (max-width: 500px) {
        bottom: 0px;
    }
`;
const Arrow = styled.img`
    text-align: right;
    margin-left: 5px;
    @media only all and (max-width: 500px) {
        width: 21px;
        height: 30px;
        margin-bottom: 3px;
    }
`;
const Button = styled.button`
    width: 100%;
    font-style: normal;
    font-weight: bold;
    font-size: 17px;
    text-transform: uppercase;
    border-radius: 10px;
    border: none;
    outline: none;
    background: none;
    margin-top: -6px;
    background: #D3D3D3;
    color: #000000;
    text-align: left;
    padding-left: 15px;
    margin-bottom: 50px;
    @media only all and (max-width: 500px) {
        margin-bottom: 30px;
        @media only all and (min-width: 401px) {
           margin-top: 0px;
        }
    }
`;
const Container = styled.div`
@media only all and (max-width: 1440px) {
	max-width: 100%!important;
	flex: 0 0 100%!important;
}
${props => props.mar && css`
        @media only all and (max-width: 500px) {
            margin-bottom: 50px;
        }
    `}
`;
const CarWrap = styled.div`
    position: relative;
    width: 100%;
    height: 234px;
    overflow: hidden;
    border-radius: 5px;
`;
const Car = styled.img`
    width: 100%;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
`;
const ContainerPadding = styled.div`
    @media only all and (max-width: 500px) {
        padding-left: 0;
        padding-right: 0;
    }
`;
function Short_Rent_Benefits() {
    return(
        <>
        <Title/>
        <div className="row">
            <Container className="col-sm-8 mx-auto">
                <RedRectangle>
                    <Logo src="./static/images/logo4.svg"></Logo>
                    <Text>Senza fatica e senza pensieri, da PALPONAUTO risolvi in modo immediato</Text>
                    <Text>Noleggia un giorno, un mese, un anno</Text>
                    <Text none>Come vuoi quando vuoi tu</Text>
                </RedRectangle>
            </Container>
            <Container className="col-sm-8 mx-auto row">
                <ContainerPadding className="col-sm-6">
                    <Link to="/Contatti">
                        <BigButton one>
                            <TitleButton>VANTAGGI NOLEGGIO A BREVE TERMINE PER AZIENDE</TitleButton>
                            <Scopri>Scopri di più<Arrow src="./static/images/arrow3.svg"></Arrow></Scopri>
                        </BigButton>
                    </Link>
                </ContainerPadding>
                <ContainerPadding className="col-sm-6">
                    <Link to="/Contatti">
                        <BigButton>
                            <TitleButton>VANTAGGI NOLEGGIO A BREVE TERMINE PER PRIVATI</TitleButton>
                            <Scopri>Scopri di più<Arrow src="./static/images/arrow3.svg"></Arrow></Scopri>
                        </BigButton>
                    </Link>
                </ContainerPadding>
            </Container>
        </div>
        <Container className="row col-sm-8 mx-auto row text-center" mar>
            <ContainerPadding className="col-sm-4">
                <div className="mx-auto"> 
                    <CarWrap>
                        <Car src="/static/images/Rectangle_94.png"></Car>
                    </CarWrap>
                    <Link to="/Contatti"><Button>offerte<br></br> noleggio furgoni BREVE TERMINE</Button></Link>
                </div> 
            </ContainerPadding>
            <ContainerPadding className="col-sm-4">  
                <div className="mx-auto">
                    <CarWrap>
                        <Car src="/static/images/Rectangle_93.png"></Car>
                    </CarWrap>
                    <Link to="/Contatti"><Button>offerte<br></br> noleggio auto BREVE TERMINE</Button></Link>
                </div> 
            </ContainerPadding>
            <ContainerPadding className="col-sm-4">  
                <div className="mx-auto">
                    <CarWrap>
                        <Car src="/static/images/Rectangle_95.png"></Car>
                    </CarWrap>
                    <Link to="/Contatti"><Button>offerte<br></br> noleggio moto BREVE TERMINE</Button></Link>
                </div>
            </ContainerPadding>
        </Container>
        <CartRow date={testing_date}/>
        </>
    );
}
export {Short_Rent_Benefits};
