import React, { useState } from "react";
import Cookies from 'js-cookie';
export default function Form() {
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [phone, setPhone] = useState("");
    /*
    const [checkbox1, setCheckbox1] = useState(false);
    const [checkbox2, setCheckbox2] = useState(false);
    */
    let formField = new FormData();
    async function AddClient() {
        const csrftoken = Cookies.get('csrftoken');
        formField.append("name", name);
        formField.append("email", email);
        formField.append("phone", phone);
        setName("");
        setEmail("");
        setPhone("");
        /*
        setCheckbox1(false);
        setCheckbox2(false);
        */
        await fetch("api/form/", {
            method: 'POST',
            headers: { "X-CSRFToken": csrftoken },
            body: formField
        }).then((response) => {console.log(response.data);})
    }
    return(
    <>
    <form>
        <div className="footer__form">
            <div className="footer__form__warning1">
                <img className="footer__form__icon1" src="/static/images/info.png" alt=""></img>
                <div className="footer__form__content">
                    <div className="footer__form__sup">Per avere un preventivo su misura per te:<br className="br"></br> Compila i campi qui sotto, sarai contattato prima possibile</div>
                </div>
            </div>
                <div className="footer__form__item">
                    <img className="footer__form__photo" src="/static/images/user.png" alt=""></img>
                    <div className="footer__form__content">
                        <div className="footer__form__name">Nome</div>
                        <div className="footer__form__line"><input name="name" value={name} onChange={e => setName(e.target.value)}></input></div>
                    </div>
                </div>
                <div className="footer__form__item">
                    <img className="footer__form__photo" src="/static/images/mail-red.png" alt=""></img>
                    <div className="footer__form__content">
                        <div className="footer__form__name">E-mail</div>
                        <div className="footer__form__line"><input type="email" name="email" value={email} onChange={e => setEmail(e.target.value)}></input></div>   
                    </div>
                </div>
                <div className="footer__form__item">
                    <img className="footer__form__photo" src="/static/images/iphone.png" alt=""></img>
                    <div className="footer__form__content">
                        <div className="footer__form__name">Telefono</div>
                        <div className="footer__form__line"><input type="number" id="phone" name="phone"  value={phone} onChange={e => setPhone(e.target.value)} required></input></div>
                    </div> 
                </div>
                <div className="footer__form__celect">
                    <div className="footer__form__flag"><input type="checkbox" name="Link" value="#" /*checked={checkbox1} onChange={setCheckbox1(true)}*/></input></div>
                    <div className="footer__form__content">
                        <div className="footer__form__text">Autorizzazioni al contatto</div>
                    </div>
                </div>
                <div className="footer__form__celect">
                    <div className="footer__form__flag"><input type="checkbox" name="Link" value="#" /*checked={checkbox2} onChange={setCheckbox2(true)}*/></input></div>
                    <div className="footer__form__content">
                        <div className="footer__form__text">ACCETTO l'informativa e i consensi ai trattamenti sulla privacy.</div>
                    </div>
                </div>
                <div className="footer__form__warning2">
                    <img className="footer__form__icon2" src="/static/images/info.png" alt=""></img>
                    <div className="footer__form__content">
                        <div className="footer__form__sub">Non tutti i consensi sono obbligatori, <a href="#">Clicca qui per gestirli</a> </div>
                    </div>
                </div>
            </div>
            <div className="button_block"><input type="button" value="RICHIEDI IL TUO PREVENTIVO" onClick={AddClient}></input></div>      
    </form>
    </>
    );
}
