import React from "react";
import {Title} from './title';
import styled, { css } from 'styled-components';
import {CartRow} from './cart';
const testing_date = [
    {id:1, term:"lungo", img:"images/cart_car1.png", name:"Toyota Auris Hibrid 1.0 Touring sport", per_mounth:1, anticipo:1, durata:36, km_inc:30000 },
    {id:2, term:"lungo", img:"images/cart_car1.png", name:"Toyota Auris Hibrid 1.0 Touring sport", per_mounth:1, anticipo:1, durata:36, km_inc:30000 },
    {id:3, term:"lungo", img:"images/cart_car1.png", name:"Toyota Auris Hibrid 1.0 Touring sport", per_mounth:1, anticipo:1, durata:36, km_inc:30000 },
 ]
const RedRectangle = styled.div`
    background: #81100B;
    border-radius: 10px;
    text-align: center;
    padding: 42px 32px;
    margin-top: 72px;
    margin-bottom: 32px;
`;
const Text = styled.div`
    font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Arial, sans-serif;
    font-style: normal;
    font-weight: normal;
    font-size: 28px;
    letter-spacing: 0.05em;
    color: #FFFFFF;
    padding-bottom: 40px;
    ${props => props.none && css`
        padding-bottom: 0px;
    `}
`;
const Logo = styled.img`
    position: absolute;
    right: -4.3%;
    top: -7%;
    z-index: 1;
    text-align: right;
    @media only all and (max-width: 1440px) {
        right: 0%;
        top: -7%;
    }
`;
const BigButton = styled.button`
    background: #D3D3D3;
    font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Arial, sans-serif;
    font-style: normal;
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    border-radius: 10px;
    border: none;
    outline: none;
    height: 161px;
    margin-bottom: 56px;
    width: 100%;
    ${props => props.one && css`
        margin-right: 30px;
    `}
`;
const TitleButton = styled.div`
    text-align: center;
    font-weight: bold;
    font-size: 32px;
    line-height: 38px;
    letter-spacing: 0.05em;
    text-transform: uppercase;
    color: #000000;
`;
const Scopri = styled.div`
    text-align: right;
    text-decoration-line: underline;
    text-transform: uppercase;
    font-weight: bold;
    font-size: 24px;
    position: relative;
    bottom: -20px;
`;
const Arrow = styled.img`
    text-align: right;
    margin-left: 5px;
`;
const Button = styled.button`
    width: 350px;
    font-style: normal;
    font-weight: bold;
    font-size: 17px;
    text-transform: uppercase;
    border-radius: 10px;
    border: none;
    outline: none;
    background: none;
    margin-top: 0px;
    background: #D3D3D3;
    color: #000000;
    text-align: left;
    padding-left: 15px;
    margin-bottom: 50px;
`;
const Container = styled.div`
@media only all and (max-width: 1440px) {
	max-width: 100%!important;
	flex: 0 0 100%!important;
}
`;
function Short_Rent_Benefits() {
    return(
        <>
        <Title/>
        <div className="row">
            <Container className="col-sm-8 mx-auto">
                <RedRectangle>
                    <Logo src="./images/logo4.svg"></Logo>
                    <Text>Senza fatica e senza pensieri, da PALPONAUTO risolvi in modo immediato</Text>
                    <Text>Noleggia un giorno, un mese, un anno</Text>
                    <Text none>Come vuoi quando vuoi tu</Text>
                </RedRectangle>
            </Container>
            <Container className="col-sm-8 mx-auto row">
                <div className="col-sm-6">
                    <BigButton href="#" one>
                        <TitleButton>VANTAGGI NOLEGGIO A BREVE TERMINE PER AZIENDE</TitleButton>
                        <Scopri>Scopri di più<Arrow src="./images/arrow3.svg"></Arrow></Scopri>
                    </BigButton>
                </div>
                <div className="col-sm-6">
                    <BigButton href="#">
                        <TitleButton>VANTAGGI NOLEGGIO A BREVE TERMINE PER PRIVATI</TitleButton>
                        <Scopri>Scopri di più<Arrow src="./images/arrow3.svg"></Arrow></Scopri>
                    </BigButton>
                </div>
            </Container>
        </div>
        <Container className="row col-sm-8 mx-auto row">
            <div className="col-sm-4">
                <div className="mx-auto"> 
                    <img src="images/Rectangle_94.png"></img>
                    <a href=""><Button>offerte<br></br> noleggio furgoni BREVE TERMINE</Button></a>
                </div> 
            </div>
            <div className="col-sm-4">  
                <div className="mx-auto text-center">
                    <img src="images/Rectangle_93.png"></img>
                    <a href=""><Button>offerte<br></br> noleggio auto BREVE TERMINE</Button></a>
                </div> 
            </div>
            <div className="col-sm-4">  
                <div className="mx-auto text-right">
                    <img src="images/Rectangle_95.png"></img>
                    <a href=""><Button>offerte<br></br> noleggio moto BREVE TERMINE</Button></a>
                </div>
            </div>
        </Container>
        <CartRow date={testing_date}/>
        </>
    );
}
export {Short_Rent_Benefits};
