import React, { useState, useEffect } from "react";
import styled, {css} from 'styled-components';
import { Link, useParams } from "react-router-dom";
import Form from "./form";
import CartRowBreve from "./cart_breve";
const Back = styled.div`
    width: 100%;
    height: 100%;
    background: #F1EBE3;
    margin-top: -50px;
`;
const Container = styled.div`
    font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Arial, sans-serif;
    color: #000000;
    text-align: center;
    padding-bottom: 60px;
    padding-top: 50px;
    @media only all and (max-width: 570px) {
        margin-top: 95px;
    }
    flex: 0 0 70%;
    max-width: 70%;
    position: relative;
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    margin-left: auto;
    margin-right: auto;    
    @media only all and (max-width: 1680px) {
        max-width: 80%!important;
        flex: 0 0 80%!important;
    }
    @media only all and (max-width: 1440px) {
    	max-width: 100%!important;
    	flex: 0 0 100%!important;
    }
`;
const ColonOne = styled.div`
    flex: 0 0 41.666667%;
    max-width: 41.666667%;
    position: relative;
    width: 100%;
    padding-right: 40px;
    margin-top: 253px;
    @media only all and (max-width: 570px) {
        max-width: 100%!important;
        flex: 0 0 100%!important;
        padding-right: 20px;
        padding-left: 20px;
        margin-top: 0px;
   }
`;
const ColonTwo = styled.div`
    flex: 0 0 58.333333%;
    max-width: 58.333333%;
    position: relative;
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    padding-left: 10px;
    padding-right: 10px;
    @media only all and (max-width: 570px) {
        max-width: 100%!important;
        flex: 0 0 100%!important;
        padding-left: 20px;
        padding-right: 20px;
        padding-top: 40px;
    }
`;
const BlockCambia = styled.div`
    display: flex;
    width: 100%;
    justify-content: end;
    @media only all and (max-width: 570px) {
        justify-content: center;
    }
`;
const CarWrap = styled.div`
    height: 317px;
    overflow: hidden;
    width: 100%;
    position: relative;
`;
const Car = styled.img`
    width: 100%;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
`;
const Line = styled.div`
    background: #8D120C;
    height: 10px;
    margin-left: 80px;
    @media only all and (max-width: 570px) {
        margin-left: 0px;
    }
`;
const CarName = styled.div`
    font-size: 24px;
    margin-right: 10px;
    font-weight: 700;
    @media only all and (max-width: 570px) {
        font-size: 20px;
    }
`;
const Section = styled.div`
    color: #000000;
    text-align: center;
    margin-left: auto;
    margin-right: auto;
    @media only all and (max-width: 570px) {
        display: none;
        &{
            display: none;
        }
    }
`;
const TitleStyle = styled.div`
    font-size: 40px;
    text-transform: uppercase;
    font-family: 'Poppins', sans-serif;
    color: #8D120C;
    font-weight: 900;
    @media only all and (max-width: 570px) {
        font-size: 40px;
    }
`;
const Subtitle = styled.div`
    font-size: 14px;
    & > span{
        color: #8D120C; 
    }
    @media only all and (max-width: 570px) {
        font-size: 18px;
    }
`;
const Label = styled.div`
    font-size: 18px;
    text-transform: uppercase;
    font-weight: 700;
    color: #FFFFFF;
    background: #232323;
    border-radius: 10px;
    padding-left: 60px;
    padding-right: 60px;
    padding-bottom: 15px;
    padding-top: 15px;
    margin-top: 30px;
    margin-bottom: 20px;
`;
const FourSquares = styled.div`
    width: 100%;
    display: flex;
    flex-wrap: wrap;

`;
const Square = styled.div`
    flex: 0 0 50%;
    max-width: 50%;
    position: relative;
    padding: 15px;
    & > div{
        height: 230px;
        border-radius: 50px;
    }
    ${props => props.one && css`
        & > div{
            background: #FF7A00;
        }  
    `}
    ${props => props.two && css`
        & > div{
            background: #84D268;  
        } 
    `}
    ${props => props.tree && css`
        & > div{
            background: #8BC5F3; 
        }
    `}
    ${props => props.four && css`
        & > div{
            background: #DAA520;
