import React, { useState, useEffect } from "react";
import styled, {css} from 'styled-components';
import { useParams } from "react-router-dom";
import Form from "./form";
import CartRow from './cart';
const Back = styled.div`
    width: 100%;
    height: 100%;
    background: #F1EBE3;
    margin-top: -50px;
`;
const Container = styled.div`
    font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Arial, sans-serif;
    color: #000000;
    text-align: center;
    padding-bottom: 60px;
    padding-top: 50px;
    @media only all and (max-width: 570px) {
        margin-top: 95px;
    }
    flex: 0 0 70%;
    max-width: 70%;
    position: relative;
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    margin-left: auto;
    margin-right: auto;    
    @media only all and (max-width: 1680px) {
        max-width: 80%!important;
        flex: 0 0 80%!important;
    }
    @media only all and (max-width: 1440px) {
    	max-width: 100%!important;
    	flex: 0 0 100%!important;
    }
`;
const ColonOne = styled.div`
    flex: 0 0 41.666667%;
    max-width: 41.666667%;
    position: relative;
    width: 100%;
    padding-right: 40px;
    margin-top: 150px;
    media only all and (max-width: 1200px) {
        max-width: 100%!important;
        flex: 0 0 100%!important;
    }
`;
const ColonTwo = styled.div`
    flex: 0 0 58.333333%;
    max-width: 58.333333%;
    position: relative;
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    padding-left: 10px;
    padding-right: 10px;
    media only all and (max-width: 1200px) {
        max-width: 100%!important;
        flex: 0 0 100%!important;
    }

`;
const BlockCambia = styled.div`
    display: flex;
    width: 100%;
    justify-content: end;
`;
const Cambia = styled.div`
    font-weight: bold;
    font-size: 18px;
    letter-spacing: 0.05em;
    color: #01579B;
    text-align: right!important;
    background: #DAA520;
    border-radius: 45px;
    padding-top: 30px;
    padding-right: 40px;
    padding-bottom: 30px;
    padding-left: 40px;
    margin-bottom: 15px;
    margin-right: -20px;
    @media only all and (max-width: 570px) {
        font-size: 14px;
    }
`;
const CarWrap = styled.div`
    height: 317px;
    overflow: hidden;
    width: 100%;
    position: relative;
`;
const Car = styled.img`
    width: 100%;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
`;
const Line = styled.div`
    background: #8D120C;
    height: 10px;
    margin-left: 80px;
`;
const CarName = styled.div`
    font-size: 24px;
    margin-right: 5px;
`;
const Section = styled.div`
    color: #000000;
    text-align: center;
    margin-left: auto;
    margin-right: auto;
    @media only all and (max-width: 570px) {
        margin-left: 20px;
        margin-right: 20px;
        margin-top: 95px;
    }
`;
const TitleStyle = styled.div`
    font-size: 40px;
    text-transform: uppercase;
    font-family: 'Poppins', sans-serif;
    color: #8D120C;
    font-weight: 900;
    @media only all and (max-width: 570px) {
        font-size: 40px;
    }
`;
const Subtitle = styled.div`
    font-size: 14px;
    & > span{
        color: #8D120C; 
    }
    @media only all and (max-width: 570px) {
        font-size: 18px;
    }
`;
const Label = styled.div`
    font-size: 18px;
    text-transform: uppercase;
    font-weight: 700;
    color: #000000;
    background: #FFD700;
    border-radius: 10px;
    padding-left: 60px;
    padding-right: 60px;
    padding-bottom: 15px;
    padding-top: 15px;
    margin-top: 30px;
    margin-bottom: 20px;
`;
const FourSquares = styled.div`
    width: 100%;
    display: flex;
    flex-wrap: wrap;

`;
const Square = styled.div`
    flex: 0 0 50%;
    max-width: 50%;
    position: relative;
    padding: 15px;
    & > div{
        height: 230px;
        border-radius: 50px;
